css:
@-moz-document domain("mobilism.org") {
* {
	font-family: open sans condensed !important;
	font-weight: 600 !important;
	border-collapse: separate !important;
	border: none !important;
	outline: none !important;
	font-size: 20px;
	text-shadow: 1px 2px 0px rgba(0, 0, 0, .3) !important;
	outline-color: transparent !important;
	}

body {
	color: #F44336 !important;
	background-color: #191919 !important;
	font-family: open sans condensed !important;
	font-weight: 600!important;
	font-size: 16px!important;
	background-image: none;
	}

.qtip-title {
	font-family: open sans condensed !important;
	font-size: 26px !important;
	padding: 0px !important;
	border-width: 0px !important;
	border-style: ridge !important;
	border-color: #333!important;
	/*     border-bottom: 12px solid #2d2d2d !important; */
	
	color: #f44336 !important;
	zoom: 0;
	background: #202020 !important;
	visibility: hidden;
	height: 0px !important;
	}

.qtip * {
	color: #e0dfd5 !important;
	font-size: 22px !important;
	background: #191919 !important;
	}

.qtip-content {
	color: #e0dfd5 !important;
	padding: 22px !important;
	overflow: hidden;
	font-size: 22px !important;
	text-align: left;
	background: #191919 !important;
	border-width: 8px !important;
	border-style: ridge !important;
	border-color: #333!important;
	}

.qtip-defaults {
	border-radius: 10px !important;
	position: absolute;
	width: 500px;
	display: none;
	top: 505.727px;
	left: 752.926px;
	z-index: 6011;
	box-shadow: 5px 5px 20px 0px rgba(0, 0, 0, .51) !important;
	}

#menu_row img {
	visibility: hidden;
	}

#menu_row a img {
	display: none }

#logo_row img {
	visibility: hidden;
	}

#logo_row a img {
	visibility: visible !important;
	}

#menu_row, #menu_row a {
	font-family: open sans condensed !important;
	font-size: 22px !important;
	font-weight: 600 !important;
	color: #FF9800 !important;
	background-image: none !important;
	background-repeat: repeat-x;
	background: #004a79 !important;
	height: 30px !important;
	text-shadow: 2px 3px 2px rgba(0, 0, 0, 1) !important;
	overflow: hidden !important;
	}

#menu_row a {
	vertical-align: baseline!important;
	padding: 0 7px 0px 5px !important;
	text-align: center !important;
	margin: -7px !important;
	}

#menu_row a:hover {
	background: #007cca !important;
	text-align: center !important;
	vertical-align: baseline!important;
	padding: 0 7px 0 5px !important;
	}

input, select {
	color: #ff8c00 !important;
	font-family: open sans condensed !important;
	font-size: 20px !important;
	font-weight: 600 !important;
	padding: 0 0 0 10px !important;
	border: none !important;
	background-color: #2d2d2d !important;
	}

#keywords {
	width: 400px !important;
	color: #ff8c00 !important;
	}

option {
	padding: 0!important;
	font-size: 22px !important;
	min-height: 25px!important;
	line-height: 3 !important;
	height: 32px !important;
	}

#search-box input.btnlite {
	font-weight: 500!important;
	background-color: #2d2d2d!important;
	border: 3px solid hsla(203, 100%, 50%, 0) !important;
	cursor: pointer;
	color: #ff8c00 !important;
	border-radius: 15%;
	padding: 0px 9px !important;
	text-shadow: 1px 2px 0px rgba(0, 0, 0, 1) !important;
	font-size: 20px !important;
	}

#search-box input.btnlite:hover {
	color: #ff8c00 !important;
	border: 3px solid hsl(203, 100%, 50%) !important;
	background-color: #2d2d2d!important;
	}

#wrapcentre {
	margin: 30px 25px 0 25px;
	}

.spacer {
	visibility: hidden !important;
	}

#pageheader {
	text-align: center;
	height: 0px;
	vertical-align: middle !important;
	}

h2 a.titles {
	font-family: open sans condensed !important;
	font-weight: 600 !important;
	;
	font-size: 33px!important;
	text-decoration: none;
	text-transform: uppercase !important;
	}

h2 {
	-webkit-margin-before: 0px !important;
	-webkit-margin-after: 0px !important;
	-webkit-margin-start: 0px !important;
	-webkit-margin-end: 0px !important;
	font-weight: bold;
	}

h3 {
	font-size: 30px;
	font-family: open sans condensed !important;
	text-transform: inherit !important;
	}

a.forumlink, a {
	color: #009dff !important;
	font-weight: 600 !important;
	font-family: open sans condensed !important;
	font-size: 22px!important;
	text-shadow: 1px 2px 0px rgba(0, 0, 0, .3) !important;
	}

a:hover {
	color: #F44336 !important;
	text-decoration: none!important;
	}

a:active, a:visited {
	text-decoration: none !important;
	color: #F44336;
	}

a:link {
	color: #009dff!important;
	text-decoration: none;
	}

#page-body {
	margin: -5px 0;
	clear: both;
	}

th, th a {
	color: #ffffff!important;
	font-size: 22px !important;
	font-weight: 600 !important;
	background: -webkit-linear-gradient(top, #004a79, #0075be) !important;
	line-height: 1.45 !important;
	background-color: #009dff !important;
	
	white-space: nowrap;
	padding: 0px 5px 0px 5px;
	text-shadow: 1px 2px 1px rgb(0, 0, 0) !important;
	}

.row1, .row2 {
	background-color: #2d2d2d;
	padding: 7px;
	}
/* .row2 {
    background-color: #161616 !important;
    padding: 7px;
} */

.row3 {
	background: -webkit-linear-gradient(top, #004a79, #0075be) !important;
	
	background-repeat: repeat-x;
	padding: 4px;
	color: #ffffff!important;
	}

.cat {
	height: 28px;
	margin: 0;
	padding: 0;
	border: 0;
	background: -webkit-linear-gradient(top, #004a79, #0075be) !important;
	background-color: #c7d0d7;
	color: #ffffff!important;
	
	text-indent: 4px;
	}

.cat a {
	color: #ffffff!important;
	text-shadow: 2px 2px 1px rgba(0, 0, 0, 1) !important;
	}

.cat input {
	margin: 5px 0 !important;
	padding: 0 20px !important;
	}

.tablebg {
	background-color: #222222 !important;
	margin-top: -5px !important;
	border-spacing: 2px!important;
	}

.table td {
	font-family: open sans condensed !important;
	font-size: 16px!important;
	font-weight: 500!important;
	}

td.last-color, td.stats-color {
	min-width: 100px!important;
	text-align: center!important;
	}

.list-group-item > span {
	color: #169FE6;
	line-height: 18px !important;
	display: inline-table;
	font-weight: 500 !important;
	margin-left: 20px;
	margin-right: auto;
	max-width: 110px;
	font-size: 15px!important;
	font-family: open sans condensed !important;
	}

.list-group-item > strong {
	display: inline-block;
	font-family: open sans condensed !important;
	font-weight: 600 !important;
	text-transform: uppercase;
	font-size: 15px!important;
	margin-right: -10px;
	margin-left: 45px;
	padding: 0;
	text-align: right;
	width: 5em;
	}

.rank-title {
	font-style: italic;
	font-size: 16px!important;
	font-family: open sans condensed !important;
	}

td.profile {
	padding: 4px;
	background: #161616;
	}

.user-profile-avatar {
	margin: -10px -15px 30px;
	text-align: center;
	width: 100%;
	}

.postbody {
	font-size: 24px !important;
	line-height: 1.2em !important;
	color: #e0dfd5;
	background: #161616!important;
	text-shadow: 2px 2px 2px rgba(0, 0, 0, 1) !important;
	padding: 30px;
	}

.gensmall div, .gensmall div b {
	margin: 1px 1px;
	font-size: 24px !important;
	}

.nav {
	margin: 0;
	color: #ff8c00 !important;
	font-weight: 600;
	}

.postauthor {
	color: #ff8c00 !important;
	font-size: 24px !important;
	}

p.breadcrumbs {
	margin: 0;
	float: left;
	color: white !important;
	font-weight: bold;
	white-space: normal;
	font-size: 1.1em !important;
	}

.postdetails {
	font-size: 24px !important;
	line-height: 1.2em !important;
	color: #e0dfd5;
	background: #161616!important;
	text-shadow: 2px 2px 2px rgba(0, 0, 0, 1) !important;
	}

.postbody .content, .hidden-phone {
	color: #495d60;
	-webkit-font-smoothing: antialiased;
	-webkit-text-size-adjust: 100%!important;
	font-family: open sans condensed !important;
	font-size: 18px !important;
	line-height: 1.4 !important;
	margin-bottom: 10px;
	background: #161616!important;
	}

.dropdown-menu a, .submenu a {
	clear: both;
	color: #6A6A6A;
	display: block;
	font-family: open sans condensed !important;
	font-size: 17px;
	font-weight: 600!important;
	padding: 8px 10px;
	white-space: nowrap;
	}

.postbody blockquote {
	font-size: 18px!important;
	font-weight: 500!important;
	background-color: #ddd;
	padding: 20px 30px 20px 30px;
	margin: 40px 20px;
	border-radius: 12px;
	font-style: italic;
	line-height: 25px !important;
	font-family: roboto !important;
	color: #444;
	border: 0px;
	box-shadow: 2px 4px 10px rgba(0, 0, 0, .6);
	}

.postbody .hidden-phone * {
	font-size: 100% !important;
	}

small, .sub-stats-info, .sub-stats {
	font-size: 20px!important;
	font-family: open sans condensed !important;
	font-weight: 600!important;
	}

small, .sub-stats-info {
	color: #777 !important;
	line-height: 22px !important;
	font-size: 20px !important;
	}

a.topictitle, a.forumtitle {
	font-family: open sans condensed !important;
	font-weight: 600!important;
	font-size: 24px !important;
	text-shadow: 1px 3px 4px rgba(0, 0, 0, 1) !important;
	}

.username-coloured, .username {
	font-size: 20px !important;
	font-family: open sans condensed !important;
	font-weight: 600!important;
	display: inline;
	}

p.topicdetails, p.topicdetails a, p.topictitle a {
	margin: 1px 0;
	font-size: 14px!important;
	font-family: open sans condensed !important;
	}

p.topictitle {
	width: 150px !important;
	padding: 0px 10px !important;
	}
}

@-moz-document url-prefix("http://forum.mobilism.org/ucp.php?i=") {
td a img {
	overflow: hidden;
	border: 0px solid #C0C0C0!important;
	display: none !important;
	box-sizing: border-box;
	padding: 0px;
	visibility: hidden!important;
	}
}

@-moz-document url-prefix("http://forum.mobilism.org/viewtopic") {
 .row2 {
 	background-color: #161616 !important;
 	margin-top: -5px !important;
 	border-spacing: 2px!important;
 	}
 
 h2 a.titles {
 	font-family: open sans condensed !important;
 	font-weight: 600 !important;
 	font-size: 37px!important;
 	text-decoration: none;
 	color: #eaed00 !important;
 	text-transform: capitalize;
 	}
}
