css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bitofsteam.com") {

    .navbar li a {
        padding: 10px;
    }

    .navbar img {
        height: 25px;
        width: 25px;
        vertical-align: middle;
    }

    .navbar .navbar-nav:not(a) li {
        top: 7px;
    }

    .navbar .navbar-right {
        margin-top: -1px;
        vertical-align: middle;
    }

    .navbar a.navbar-right {
        margin-top: 0px;
    }

    .navbar .container:after {
        clear: both;
    }

    .navbar .dropdown-menu {
        z-index: 10;
        top: 46px;
    }

    .navbar .dropdown-menu li {
        padding: 0px;
    }

    .navbar li.dropdown {
        margin-left: 10px;
    }

}
