css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://mudcat.org/thread.cfm"),
               url-prefix("https://mudcat.org/thread.cfm")
/* mudcat - magnify posts: Increases post font size for viewing for a phone */
/* Version 2 13-10-2017 */
/* Include TLS pages */
{
/* Increase font size of posts */
table {font-size: 200% ;}
body > table:nth-child(2) {font-size: 100% ! important ;}
/* Normalise font size of top section - related threads & post list */
body > table:nth-child(2) > tbody > tr > td:nth-child(1) > table  {font-size: 100% ! important ;}
body > table:nth-child(2) > tbody > tr > td:nth-child(2) table {font-size: 100% ! important ;}
/* Normalise font size of bottom section - share and post box */
body > table[bgcolor="NAVY"] {font-size: 100% ! important ;}
body > table[width="640"] {font-size: 100% ! important ;}
/* Suppress inserted adverts */
ins {display:none ! important ;}
}
