@-moz-document url-prefix("about:reader") {
   
*::-moz-selection {
        background: #b4e7f8;
}
    body {
        color: black !important;
        background-color: #FFFFFF !important;
        text-rendering: optimizeLegibility!important;
    }
    body.sepia {
        background-color: hsl(33, 17%, 85%)!important;
        color: hsl(33, 40%, 6%);
    }
    body.dark {
        background-color: hsl(0, 0%, 8%)!important;
        color: hsl(0, 0%, 72%);
    }
    
    body.sans-serif,
    body.sans-serif .remove-button {
        font-family: "Open Sans", sans-serif;
    }
    
    body.serif,
    body.serif .remove-button  {
        font-family: "Lora", serif;
    }
    
    h1 {
        font-family: "Open Sans", sans-serif;
        text-align: center !important;
        font-weight: 600!important;
        font-size: 2em!important;
        line-height: 1.1!important;
    }
    
    .credits {
        text-align: center !important;
        margin-bottom: 0px !important;
    }
    
    #container {
        text-align: justify !important;
        width: 90% !important;
    }
    
    .content div {
        padding: .3rem 0 .8rem!important;
    }
    .content br {
        line-height: 1ex!important;
    }
    
    .caption, .wp-caption-text, figcaption {
        font-size: .8em !important;
        text-align: justify !important;
    }
    
    img {
        border-color: #e1e1e1;
        border-style: solid;
        border-width: 1px;
        line-height: 1;
        margin: 0 auto;
        padding: 0.25em;
        text-align: center;
    }

    figure 
    {
        display: table !important;
        margin: 0 auto !important;
        margin-bottom: 10px !important;
        padding: 15px !important;      
    }
   
    img, figcaption 
    {
        display: table-caption;
        caption-side: bottom;
    }
    
    a {
        color: black !important;
        text-decoration: none !important;  
        border-bottom: 3px solid #b4e7f8;
        box-shadow: inset 0 -4px 0 #b4e7f8;
        -moz-transition: background .15s cubic-bezier(.33,.66,.66,1);
    }  
    
    a:hover {
        background:#b4e7f8
    }
    
    .content br:-moz-any(:-moz-first-node,
    :-moz-last-node),
    .content:not(img):not(br):-moz-only-whitespace,
    .content:-moz-any(p,
    a,
    div,
    td):empty,
    .domain{
        display: none!important
    }
}
