css:
@-moz-document domain("starcitizen.tools") {
#mw-page-base, #mw-head-base, .h1, table.mw_metadata td, table.mw_metadata th, pre {
    background-color: #101010 !important;
    border: 1px solid #101010 !important;
    color: grey;
}
.mw-body h1, .mw-body h2, .mw-body h3, .mw-body h4, .mw-body h5, h1, h2, h3, h4, h5, firstHeading, code {
    border: #1b222d;
    color: #fff;
}
td, th, html body #content #bodyContent #mw-content-text table tbody tr td div, .flow-topic-titlebar, html.client-js.ve-available #mw-content-text table tbody tr td div {
    background-color: #1e1e1e !important;
    border: none !important;
}
.mw-body, #content, code, .infobox-table-img img {
    background-color: #1e1e1e !important;
    color: grey !important;
}
.wikitable, table.wikitable>*>tr>th, table.wikitable>*>tr>td, table.wikitable>*>tr>tr {
    background-color: #1e1e1e !important;
    border-bottom: 1px solid #404040 !important;
    border-left: none !important;
    border-right: none !important;
    border-top: none !important;
    color: grey;
}
/* BG and tables */
.mw-head, #toc, div.toc, ul#filetoc, li.gallerybox div.thumb, .mw-ui-button, html body #content #bodyContent #mw-content-text #toc div#toctitle h2, #toc, #editpage-copywarn p, .infobox-tableinfobox-table, #p-logo, #mw-panel, table.infobox-table, div#mw-panel div.portal div.body ul li a, html, .boilerplate, .table, div#mw-panel div.portal h3, .toctitle, .editOptions, #editpage-copywarn, .portal, .flow-component.flow-board-page .flow-board-header, .mw-ui-button.mw-ui-quiet, tbody, html.client-js.ve-available table tbody tr td table tbody tr th, body {
    background-color: #101010 !important;
    color: grey !important;
}
div#mw-panel {
    overflow: hidden
}
div#mw-panel div.portal h3, #p-logo {
    border-bottom: 1px solid #1e1e1e;
}
.flow-post {
    color: grey !important;
}
/* editor */
input, textarea, input#wpSave,  textarea:focus, input:focus {
    color: grey !important;
    background-color: #101010;
    border: 1px solid #101010;
}
textarea:focus{
    border: 1px solid cyan;
}
#toolbar {
    background-color: #505050;
}
#wpTextbox1 {
	height: 650px;
}
/* logo */
.mw-wiki-logo {
    background-image: url(https://starcitizen.tools/images/8/88/Newlogo.png) !important;
}
/* infobox */
table.infobox-table {
    width: 43%;
    right: -4.6em !important;
    top: auto !important;
    padding: 0 !important;
    margin-top: -9em !important;
    margin-bottom: 5em !important;
}
/* existing link */
a:link {
    color: #6783ab;
}
/* Broken link */
a.new, #p-personal a.new {
    color: #dd4444;
}
/* visited link */
a:visited {
    color: #6783ab;
}
/* mouse over link */
a:hover {
    color: #6783ab;
}
/* selected link */
a:active {
    color: #6783ab;
}
/* footers */
div#footer, div#footer ul li, #footer a {
    background-color: #0a1b2c;
    color: grey;
}
div#footer #footer-icons, #footer-icons:before {
    background-color: #0e2133;
    color: grey;
}
}
