@-moz-document url-prefix("https://twitter.com"), domain("twitter.com") {

/* Sahel - Open Sans https://github.com/rastikerdar */
@font-face {
    font-family: Sahel;
    font-weight: normal;
    src: url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.eot);
    src: url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.eot?#iefix) format('embedded-opentype'), url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.woff) format('woff'), url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.ttf) format('truetype');
}
@font-face {
    font-family: Sahel;
    font-weight: bold;
    src: url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Bold.eot);
    src: url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Bold.eot?#iefix) format('embedded-opentype'), url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Bold.woff) format('woff'), url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Bold.ttf) format('truetype');
}
@font-face {
    font-family: Sahel;
    font-weight: 900;
    src: url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Black.eot);
    src: url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Black.eot#iefix) format('embedded-opentype'),
url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Black.woff) format('woff'), url(https://rawgit.com/rastikerdar/sahel-font/master/dist/Sahel-Black.ttf) format('truetype');
}

@font-face {
  font-family: Parastoo;
  src: url('https://cdn.rawgit.com/rastikerdar/parastoo-font/v1.0.0-alpha3/dist/Parastoo.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/parastoo-font/v1.0.0-alpha3/dist/Parastoo.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/parastoo-font/v1.0.0-alpha3/dist/Parastoo.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/parastoo-font/v1.0.0-alpha3/dist/Parastoo.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Parastoo;
  src: url('https://rawgit.com/rastikerdar/parastoo-font/master/dist/Parastoo-Bold.eot');
  src: url('https://rawgit.com/rastikerdar/parastoo-font/master/dist/Parastoo-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/parastoo-font/1c1ccebc/dist/Parastoo-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/parastoo-font/1c1ccebc/dist/Parastoo-Bold.ttf') format('truetype');
  font-weight: bold;
}

/* Vazir https://github.com/rastikerdar */
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v4.4.0/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

/* Shabnam https://github.com/rastikerdar */
@font-face {
    font-family: Shabnam;
src: url(https://cdn.rawgit.com/rastikerdar/shabnam-font/master/dist/Shabnam.eot);
src: url(https://cdn.rawgit.com/rastikerdar/shabnam-font/master/dist/Shabnam.eot#iefix) format('embedded-opentype'), 
url(https://cdn.rawgit.com/rastikerdar/shabnam-font/master/dist/Shabnam.woff) format('woff'), url(https://cdn.rawgit.com/rastikerdar/shabnam-font/master/dist/Shabnam.ttf) format('truetype');
}

/* Samim https://github.com/rastikerdar */
font-face {
    font-family: 'Samim';
src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.eot');
src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.eot?#iefix') format('embedded-opentype'),
url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.woff') format('woff'),
url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.ttf') format('truetype');
    font-weight: normal;
}
    
/* Droid Arabic Naskh (Arabic) http://www.google.com/fonts/earlyaccess */
@font-face {
  font-family: 'Droid Arabic Naskh';
  font-style: normal;
  font-weight: 400;
  src: url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.eot);
  src: url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.eot?#iefix) format('embedded-opentype'),
       url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.woff2) format('woff2'),
       url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.woff) format('woff'),
       url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Regular.ttf) format('truetype');
}
@font-face {
  font-family: 'Droid Arabic Naskh';
  font-style: normal;
  font-weight: 700;
  src: url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.eot);
  src: url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.eot?#iefix) format('embedded-opentype'),
       url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.woff2) format('woff2'),
       url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.woff) format('woff'),
       url(https://fonts.gstatic.com/ea/droidarabicnaskh/v7/DroidNaskh-Bold.ttf) format('truetype');
}


body, body .TwitterCard {
	font-family: '/*[[font]]*/', Sahel, Parastoo, Vazir, Shabnam, Samim, 'Droid Arabic Naskh', Tahoma !important;
}
.ProfileHeaderCard-name {
    font-family:  "sahel-black", "Sahel";
    font-weight:  900;
}
blockquote, dl, dd, h1, h2, h3, h4, h5, h6, figure, p, pre {
  margin-top: 3px !important;
}
.ProfileCardStats-statLabel {
  line-height: 1.4;
}
.stream-item .content, .permalink-tweet .content {
  padding: 0 4px;
}
.ProfileHeaderCard-screenname {
  width: 100% !important;
}
.prf-follow-state {
  width: 130px;
}
.prf-stats {
  width: 310px;
}

/* --- TIMELINE --- */
div#page-container.wrapper{
width: 945px !important;
}
#timeline,
div.module.profile-card.component.profile-header, div.profile-header-inner-overlay,
.wrapper-profile .profile-card.profile-header .profile-header-inner
{
width: 630px !important;
}
body.three-col .content-main{
float:right;
}
.ProfilePage #timeline{
    width: auto !important;
}
.wrapper-profile .profile-card.profile-header .profile-header-inner {
background-repeat: repeat !important;
}
/* --- SHADOWS --- */
  .Gallery-content, #timeline,
  .stream-item.open, .global-nav-inner, .permalink {
    box-shadow: 0 0 5px black;
}
.module, .WhoToFollow, .flex-module {
    background: whitesmoke !important;
}
/* --- COLORS --- */
  #page-outer, .AppContent, .compensate-for-scrollbar, .gallery-enabled, .gallery-enabled .global-nav-inner, .grid-enabled, .grid-enabled .global-nav-inner, .modal-enabled, .modal-enabled .global-nav-inner, .overlay-enabled, .overlay-enabled .global-nav-inner, .profile-editing, .profile-editing .global-nav-inner, .ProfileHeading-content, .TwitterCard-container--clickable, body.logged-in, body:first-child, html:first-child, .ProfileCanopy-headerBg
  {
    background-color: /*[[bgcolor]]*/ !important;
  }

.tweet:hover {
    background-color: #f8f8f8;
}
.home-tweet-box {
    background-color: #f7f7f7;
}

.new-tweets-bar {
    background-color: #F0F2F3;
    border: 0.5px solid rgba(0,0,0,0.01);
}

.new-tweets-bar:hover {
    background-color: #e2e6e8;
}

.top-timeline-tweetbox .timeline-tweet-box {
    border: 1px solid rgba(0,0,0,0.1);
}
.MomentFloatingMenu {
     background-color: #f5f5f5 !important;
	 border: 1px solid #ECECEC !important;
}
.hovercard .profile-social-proof,
.ProfileCard { 
background-color:#f5f5f5 !important;
}

/* --- menu-bar --- */
.global-nav-inner {
	background: /*[[menucolor]]*/ !important;
	height: 46px;
}
.bird-topbar-etched {
	color: /*[[tweetyrbirdcolor]]*/ !important;
	 top: 10px;
}
.nav>li {
	color: /*[[menutextcolor]]*/ !important;
}
.global-nav .form-search .search-input {
	background-color: #dedede !important;
}
.global-nav .form-search .search-input.focus {
	background-color: #d3d3d3 !important;
	opacity: 1;
	-ms-filter: "alpha(opacity=100)";
}
.global-nav .search-input {
	border: 0px solid #323232;
}
.global-nav .search-icon .Icon {
	color: #7aa0cc;
}
.btn
{
	border-radius: 21px;
}
  
  
 		.dm-new { background-color: transparent !important; }
		
		.count > span,
		.dm-new > span
			{
				opacity: 1 !important;
				border-width: 1px !important;
				margin: 1px 0px 0px 1px !important;
			  border-radius: 10px !important;
			} 
				
		.dm-new > span:nth-child(1),
		.count > span:nth-child(1)
		  {
				border-color: transparent !important;
		  }
		
		.count.new-count { background-color: transparent !important; }
		.content-main #apps-stream .stream-item .app .content { padding-right: 10% !important; }
  
/* --- Circle Avatars --- */
	.DashboardProfileCard-avatarLink{
  background:none;
}
.DashboardProfileCard-avatarImage{
  border: 0px solid #fff;
}

.ProfileAvatar,.ProfileCard-avatarLink,.ProfilePopupContainer--bellbird .ProfileHeader .profile-picture{
    background:white;
    border:#fff 0px solid;
}

/*[[circleavatars]]*/											
		
/* --- Hide Who to follow --- 
  div[class*="wtf-module js-wtf-module"] {
    display: none !important;
  }
	*/
  
/* --- Hide Promoted Tweets and Ad --- */
*[data-advertiser-id], .promoted,[data-promoted],.promoted-account,#empty-timeline-recommendations,[data-nav="ads"], .suggested-tweet-stream-container, .promoted.trend-name, .promoted-tweet {display: none !important;}
	/* --- Hide Advertise with Twitter --- */
  div[class*="flex-module Footer-adsModule"] {
    display: none !important;
  }
/* --- Hide FOOTERS --- */
.Footer,
.Footer-adsModule {
    display: none !important;
}
/* --- Hide Live video --- */
  div[class="LiveVideoHomePageModule module"] {
    display: none !important;
  }
 /* Hide FOOTERS */
.Footer,
.Footer-adsModule {
    display: none !important;
}
	/* --- Spinner --- */
.FoundMediaSearch-dropdownMenu .FoundMediaSearch-pagination,.PermalinkOverlay-spinner,.DMSpinner,.show-more-spinner,#trend-locations-loading,.DMConversation-spinner,.pushstate-spinner,.spinner-bigger,.stream-end-inner .spinner,.GridTimeline .has-more-items .spinner{width:26px;height:26px;padding:0px!important;background:none!important;text-indent:-9999em;overflow:hidden;border-radius:50%;margin:6.5px auto;font-size:8px;border-top:.5em solid rgba(255, 255, 255, 0.2);border-right:.5em solid rgba(255, 255, 255, 0.2);border-bottom:.5em solid rgba(255, 255, 255, 0.2);border-left:.5em solid #09f;-webkit-transform: translateZ(0);-ms-transform: translateZ(0);transform: translateZ(0);-webkit-animation:load8 1.2s infinite linear;animation:load8 1.2s infinite linear;left:48.6%;}@-webkit-keyframes load8{0%{-webkit-transform: rotate(0deg);transform: rotate(0deg);}100%{-webkit-transform:rotate(360deg);transform: rotate(360deg);}}@keyframes load8{0%{-webkit-transform: rotate(0deg);transform:rotate(0deg);}100%{-webkit-transform:rotate(360deg);transform:rotate(360deg);}}
}
