css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('forums.redflagdeals.com') 
{

#header_leaderboard,.first_post_ad, .post_voting, .pagination_page,  .is_selected, .inactivetab, .first-post-time, .guest_intro, .site_footer_inner, #site_footer_boilerplate
  {
    display: none;
  }
  

  .forums_nav_primary_column
  {
    padding-right: 0.5rem;
  }
  
  .forums_page_header
  {
    padding-top: 0rem !important;
    padding-bottom: 0rem !important;
  }
  
  nav#site_breadcrumbs {
    height: 30px !important;
    font-family: FontAwesome;
    height: 0;
    left: -5px;
  }
  
  .forums_page_header_buttons, .site_pagination_forums, .category_filter, .forums_page_header
  {
    margin: 0 !important;
  }
  
  .forum_group, .below_postlist, .search_results_header
  {
    margin-bottom: 20px;
  }

h3 {
    font-family: Verdana, sans-serif;
}


.topiclist.header {

    margin-bottom: 10px;
    width: 100%;
    border: 1px solid #9a0000;
    background: #c00;
    color: #fff;
    float: left;
    display: block;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -webkit-border-top-right-radius: 5px;
    -webkit-border-top-left-radius: 5px;
    -moz-box-shadow: -2px 2px 2px #c8c8c8;
    -webkit-box-shadow: -2px 2px 2px #c8c8c8;  
}

.topiclist .row .row-item{
     background: linear-gradient(0deg, #eeeeee 0px, #fff 15px);

		}

 .topictitle {
        font-size: 0.780rem;
        color: #000464;
        letter-spacing: 0;
        line-height: 1.4;
    }

  .topic_title_link {
        font-size: .780rem;
        color: #000464;
}

.thread_meta_large_primary {
        color: #444;
}

.thread_info {
        font-family: Verdana, sans serif;
        font-size: 0.85em;

}

 .thread_info_title {
        margin-left: 0px;
    }

.thread_posts {
border-width: 1px; border-style: solid; margin: 0px; padding: 0px; border-color: rgb(221, 221, 221);
}

 .topiclist .posts {
        width: 8.33333%;
        float: left;
        padding-left: 0;
        padding-right: 0;
        display: block;
        text-align: center
    }

 .forum_details .forumtitle a {
        font: 14px Verdana, Calibri, Verdana, Geneva, sans-serif;
        color: #000464;
        text-decoration: none;
    }

.description {
        font-size: 0.8em;
        line-height: 0em;

}

.form_submit{
        color: #fff;

}

.forum_group_header  {
    font: 14px Tahoma, Calibri, Verdana, Geneva, sans-serif;
    display: block;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -webkit-border-top-right-radius: 5px;
    -webkit-border-top-left-radius: 5px;
    -moz-box-shadow: -2px 2px 2px #c8c8c8;
    -webkit-box-shadow: -2px 2px 2px #c8c8c8;
}
 

.thread_header_endmatter, .thread_header_taxonomy, .first_post_ad
  {
    display: none;
  }

.thread_topmatter {
        background: #fff;
        border: 1px solid #fff;
        margin-left: -15em;
        margin-bottom: -6em;
        margin-top: -0.3rem;
        position: relative
    }


 .topictitle {
        font-size: 0.780rem;
        color: #000464;
        letter-spacing: 0;
        line-height: 1.1;
        font-weight: normal;

    }


  .topic_title_link {
        font-size: .780rem;
        color: #000464;
        font-weight: normal;
}

.thread_meta_large_primary {
        color: #444;
}

.thread_info {
        font-family: Verdana, sans serif;
        font-size: 0.85em;

}

 .thread_info_title {
        margin-left: 0px;

    }


 .topiclist .posts {
        width: 8.33333%;
        float: left;
        padding-left: 0;
        padding-right: 0;
        display: block;
        text-align: center
    }

 .forum_details .forumtitle a {
        font: 14px Verdana, Calibri, Verdana, Geneva, sans-serif;
        color: #000464;
        text-decoration: none;
    }

.description {
        font-size: 0.8em;
        line-height: 1.0em;
}

.forum_group_header  {
    font: 14px Tahoma, Calibri, Verdana, Geneva, sans-serif;
    display: block;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
    -webkit-border-top-right-radius: 5px;
    -webkit-border-top-left-radius: 5px;
    -moz-box-shadow: -2px 2px 2px #c8c8c8;
}
 

.thread_header_endmatter, .thread_header_taxonomy, .first_post_ad, .actionbar
  {
    display: none;
  }

.thread_topmatter {
        background: #fff;
        border: 1px solid #fff;
        margin-left: -15em;
        margin-bottom: -6em;
        margin-top: -0.3rem;
        position: relative
    }

.postprofile {
        margin-top: -0.5em;
        margin-bottom: -0.8em;
        font-size: 1em;
    }

.post_dateline {
        font-size: .7rem;
        line-height: 1.4;
        font-weight: 500;
        height: 18px;
        line-height: 18px;
    }
 
.post_body {
font-size: 0.965em;
background: linear-gradient(to right, #eeeeee 200px, rgba(238, 238, 238, 0) 200px, transparent);
color: #000000;
background-color: rgb(250, 250, 250);
}


.post_profilearea
{
	background-color: rgb(238, 238, 238)
}
 .postprofile .profile_rank + .profile_datejoined, .postprofile .profile_username + .profile_datejoined {
        margin: 5px 0 0;
        font-size: 0.8em;
    }

 .postprofile .profile_rank + .profile_datejoined + .profile_numposts {
        margin: 5px 0 0;
        font-size: 0.8em;
    }

 .postprofile .profile_rank + .profile_datejoined + .profile_numposts + .profile_upvotes {
        margin: 5px 0 0;
        font-size: 0.8em;
    }

}
