style_setting_options.value:
/*---------navTop---------*/
@media only screen and (min-width:768px) {
    .nav {
        min-height: 30px;
        height: 30px;
        width: 100%;
        top: 0px;
    }
    .nav__links a {
        display: inline-block;
        width: 60px;
        height: 30px;
        padding-top: 7px;
    }
    .nav__logo {
        position: fixed;
        right: 0px;
        height: 30px;
        width: 30px;
        background-color: transparent;
        margin-top: 0px;
        padding-top: 0px;
        margin-right: 5px;
        top: 0px;
    }
    .nav__logo img {
        width: 30px;
        height: 30px;
    }
    .main-content {
        margin-left: 0px;
        padding-top: 30px;
    }
    .nav__footer {
        right: 40px;
    }
    .mod-btn {
        bottom: 0px;
        left: calc(100% - 220px);
    }
    .nav__sml {
        height: 30px;
        padding-top: 7px;
        width: 60px;
    }
    .nav__sml i {
        font-size: 22px;
    }
    .nav__notifications {
        border: none;
        padding-top: 0px;
        top: 0px;
        left: 0px;
        height: 30px;
        width: 60px;
        z-index: 999;
    }
    .settings__nav {
        left: 0px;
    }
    .notifications--visible {
        left: 0px!important;
    }
    .notifications {
        height: calc(100% - 30px);
        top: 30px;
    }
    .forum__nav .new-btn {
        top: 60px;
    }
    .mark-editor {
        left: 0px;
        width: 100%;
    }
    .mark-full {
        height: calc(100% - 30px);
    }
    .browse-options {
        top: 60px;
    }
    .footer {
        margin-left: 0px;
    }
    .nav__notifications > div[ng-click="notiVm.toggleNotificationView()"] {
        padding: 8px 1px 8px 1px;
    }
    .browse__nav {
        height: calc(100% - 60px);
        top: 60px;
    }
    .nav__links .material-icons {
        display: none;
    }
    .nav__links .discord-link img {
        display: none;
    }
    .nav__avatar {
        display: none;
    }
    .nav__footer .material-icons {
        font-size: 15px
    }
    .mod-btn .material-icons {
        font-size: 15px
    }
    .browse__nav {
        height: calc(100% - 30px);
        top: 30px;
    }
    .browse-options {
        top: 30px;
    }
    .forum__nav .new-btn {
        top: 30px;
    }
    .thread__pagination--fixed {
        right: 2%;
    }
    .serieslist-nav {
        width: 100%;
    }
    .mod-feed .feed__nav {
        left: 0px;
    }
    .spinner {
        padding-top: 30px;
    }
    #nprogress .bar {
        top: 30px;
    }
    .list-editor {
        top: 30px;
    }
    .list-editor .delete {
        bottom: 30px;
    }
    .nav__links > br {
        display: none;
    }
    .theme-editor .close {
        top: 30px;
    }
    .theme-editor .save.close {
        top: 70px;
    }
    .serieslist-page-nav {
        width: 100%;
        top: 80px;
    }
    .home.activity__type-btns a.changelog {
        top: 36px;
    }
    .donator {
        top: 45px;
        left: 15px;
    }
    #list_header {
        top: 30px;
    }
}

/*---------wid80---------*/
.home {
    width: 80%;
}
.home__feed {
    max-width: 67%;
    margin-left: 1%;
    margin-right: 1%;
}
.home__quicklists {
    max-width: 31%;
}
div.home__panel.home__feed i.activity-create.material-icons {
    left: 5px;
    top: -52px;
}
.col-sm-offset-2 {
    margin-left: 2%;
    margin-right: 2%;
}
.col-sm-8 {
    flex-basis: 96%;
    max-width: 96%;
}
.activity__wrap {
    padding-bottom: 0px;
}
.activity__img {
    max-height: 60px;
}
.activity__text {
    min-height: 32px;
    padding-top: 9px;
}
.thread__header .header__wrap {
    max-width: 100%;
    width: 90%;
}
.cast__banner .banner__wrap {
    max-width: 100%;
    width: 90%;
}
.cast__wrap {
    max-width: 100%;
    width: 90%;
}
.home__twitter iframe {
    width: 100% !important;
}

