css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.bombmanual.com/manual/1/html/index.html") {
  /*Position pages*/
  #ManualContent > div > .section {
    display: block;
    float:left;
  }
  
  /*Remove uncessary pages*/
  #ManualContent > div > div:nth-child(-n+2) {
    display: none !important;
  }
  
  #ManualContent > div > div > div:nth-child(n+5):nth-child(-n+7) {
    display: none !important;
  }
  
  #ManualContent > div > div > div:nth-child(n+9) {
    display: none !important;
  }

  /*Remove flavour text*/
  .flavour-text {
    display: none !important;
  }
  
  .appendix-reference {
    display: none !important;
  }
  
  .page-header-doc-title {
    display: none !important;
  }
  
  .page-footer {
    display: none !important;
  }
  
  /*On the Subject of Wires*/
  #ManualContent > div:nth-child(2) > div:nth-child(3) > div:nth-child(1) > div:nth-child(3) > ul:nth-child(4) {
    display: none !important;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(4) > div:nth-child(1) > div:nth-child(3) > p:nth-child(5) {
    display: none !important;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(8) > div:nth-child(1) > div:nth-child(3) > ul:nth-child(4) {
    display: none !important;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(7) > div:nth-child(1) > div:nth-child(3) > ol:nth-child(4) {
    display: none !important;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(7) > div:nth-child(2) > div:nth-child(3) > p:nth-child(2) {
    display: none !important;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(7) > div:nth-child(2) > div:nth-child(3) > h3:nth-child(1) {
    display: none !important;
  }
  
  .wire-sequence > div:nth-child(3) > ul:nth-child(4) {
    display: none !important;
  }
  
  .venn-wires > div:nth-child(3) > ul:nth-child(4) {
    display: none !important;
  }
  
  #venndiagram {
    top: 0px;
  }
  
  #vennlegend {
    top: 15px;
  }
  
  #venntable {
    top: 15px;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(7) > div:nth-child(2) > div:nth-child(1) {
    display: none;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(7) > div:nth-child(2) > div:nth-child(3) {
    margin: 1.4% !important;
  }
  
  .page {
    margin: 0px;
    padding: 0;
    width: 45em;
    min-height: 1em;
    box-shadow: none;
  }
  
  .page-content {
    margin: 2.7em 2.82% 0;
    min-height: 47.8em;
  }
  
  .whos-on-first-step1-table {
    margin-top: 1.8em !important;
  }
  
  .whos-on-first-step2-table {
    width: 100.8%;
  }
  
  /*Second section*/
  #ManualContent > div:nth-child(2) > div:nth-child(10) > div:nth-child(4) {
    display: block;
    float:left;
  }
  
  #ManualContent > div:nth-child(2) > div:nth-child(10) > div {
    display: block;
    float:left;
  }
}
