css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
.adcolumn, profile_sidebar_ads{display:none!important}
#feedwall_with_composer,#info_tab,#info_edits_div,#info_section_1{width:700px!important}
.creation_buttons{width:920px!important}
.photos_tab{width:920px!important;margin-left:10px;}
.UIPhotoGrid_Table {width:910px!important;}
.story_body{width:643px!important;}
#app_box_left_column{padding-left:10px!important;width:600px!important}
#app_box_right_column{float:right!important; margin-right:-170px!important;width:300px!important}

*[class="profile_sidebar_ads"],
*[id="sidebar_ads"] {display:none!important}

#home_container,.home_welcome_info, .home_main_item, body{background-color:transparent!important;}

#facebook {background-image: url(http://img228.imageshack.us/img228/7122/facebookld2.gif)!important; background-attachment: fixed !important;}

#left_column, .right_column,.message_rows{background-color:#FFFFFF !important;
-moz-border-radius: 0px 0 8px 8px;}

/*#profile_top_bar {background-color:#110000 !important}*/

.right_column_container {background-color:#ffffff !important}

.tab_content {background:transparent !important}

*[id="fb_menubar"] {background-image:none!important;
background-color:#000000 !important;
-moz-border-radius: 0px 0 8px 8px;}

#newsfeed_wrapper *, #profileimage, #profile_pic {background-color:transparent!important; -moz-border-radius:8px;color:#000!important}

*[class="profile_color_bar"], #newsfeed_wrapper, #home_sidebar{
background:url(data:image/png;base64,)!important; 
-moz-border-radius:8px; 
border: #999 1px solid;}

.home_corner,.home_side,#left_column {background:transparent!important}


.profile.chat_body.profile_two_columns {background:transparent !important}

#pagefooter,.pagefooter_topborder {background:#fff !important}

.selected {background-color:#bbbbbb !important}

A:link {color: #c0c0c0 !important}
a:visited {color: #c0c0c0 !important }
.fb_menu a:link,.fb_menu a:visited{color: #c0c0c0 !important}

}
