html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	font-family: 'Roboto', sans-serif;
}

html{
	overflow-x: hidden;
	overflow-y: scroll;
}

body{
	margin:0;
	padding:0;
	background-color: #eee;
	background-image: none;
	color: #3b3b3b;
	
}
h1,h2,h3,h4,h5,h6{
	margin-top: 0px;
	margin-bottom: 0px;
	font-weight: normal;
	color: black;
	padding: 5px 0px;
	line-height: 100%;
}


body,div,table,td{
	font-size:12px;
}

* {
	transition: all 0.15s ease-in-out;
}

*:hover {
	transition: all 0.15s ease-in-out;
}

a {
	color: #ae477b;
}
		
a:hover {
	text-decoration: underline;
}

hr {
	border-width: 1px;
	border-style: solid;
	border-color: #e5e5e5;
}

	
#pgbottom{
	text-decoration:none!important;
	height:1px;
	width:1px;
	font-size:1px;
}

.ErrorContainer{
	background: no-repeat top left #FFEDEF;
	border-top:1px solid #FFC2CA;
	padding:10px 10px 10px 27px!important;
	overflow:hidden;
}

.ErrorTitle{
	font-weight:bold;
	color:#c00!important;
}

.Error{
	color:#c00!important;
}

#Success{
	border-top:1px solid #9C9;
	border-bottom:1px solid #9C9;
	background:#E2F9E3;
	color:#060!important;
	display:block;
	line-height:200%;
	text-align:center;
	font-size:13px;
	margin-bottom:10px!important;
}
/* Custom browser CSS -----------------------------------------------------------------------*/
		:focus {outline: 0px;}
			
		::-webkit-scrollbar {
			width: 6px;
			height: 6px;
			background-color: transparent;
			border: none;
		}
		
			::-webkit-scrollbar-thumb {
				position: absolute;
				background-color: rgba(85, 85, 85, 0.5);
				transition: background-color 0.15s ease-in-out;
				border: none;
				border-radius: 2px;
			}
			
			::-webkit-scrollbar-thumb:hover {
				background-color: rgba(85, 85, 85, 1);
				transition: background-color 0.15s ease-in-out;
				border: none;
			}
			
			::-webkit-scrollbar-button {
				display: none;
				width: 0;
				height: 0;
			}
		
			::-webkit-scrollbar-corner {
				background-color: transparent;
				color: #555555;
			}
		
		::selection {background-color:#f1f1f1;color:#5b92fa;font-weight:bold;}
		/* mozilla firefox */
		::-moz-selection {background-color:#f1f1f1;color:#5b92fa;font-weight:bold;}

/* Notifications -----------------------------------------------------------------------*/
.Notice{
	background: left center no-repeat;
	background-color:  #FFFECC;
	border:1px solid #FAEBB1;
	font-size:13px;
	padding:8px 8px 8px 30px;
	margin-bottom:20px;
	color:#D57D00;
	line-height:150%;
}

.Notice span{
	float:right;
	display:block;
	font-size:11px;
}

.Notice span a,.Notice span a:link,.Notice span a:visited,.Notice span a:hover{
	color:#f60;
}

.Invisible{
	display:none;
}

.Visible{
	display:block;
}
/* Session (login and stuff) -----------------------------------------------------------------------*/
#Session{
	position: fixed;
	z-index: 4;
	top: 165px;
	right: 15px;
	padding: 15px;
	width: 270px;
	background-color: #fff;
	color: black;
	font-size: 15px;
	text-align: left;
	font-family: 'Roboto', sans-serif;
	border-radius: 2px;
	box-shadow: 0 1px 5px rgba(0,0,0,0.16);
}

#Session a{
	color: #ae477b;
	font-weight: normal;
	font-size: 15px;
}

#Session a:hover{
	text-decoration: underline;
	color: #ae477b;
}
/* Header -----------------------------------------------------------------------*/
#Header{
	height: 260px;
	width: calc(100% + 6px);
	background: #2a2b2e url("http://i.imgur.com/jRDYkbL.jpg") no-repeat;/*   Picture is from Air by Dave Ainsworth*/
	background-size: cover;
	background-position: 50% 10%;
	border-top: 0px;
	border-bottom: 0px solid;
	overflow: visible !important;
}


#Header h1{
	color:#fff;
	font-size:0px;
	text-align: left;
	margin-top: 0px;
}
#Header h1:after {
    background: url("http://i.imgur.com/a0CPCWl.png") no-repeat;
	background-size: 370px auto;
	top: 20px;
	position: absolute;
	left: 20px;
	width: 400px; 
    height: 150px;
    content:"";
	-webkit-filter: drop-shadow(0 1px 8px rgba(0,0,0,0.5));
}
#Header h1 strong{
	color:#fff;
	font-size:0px;
}

/* Menu -----------------------------------------------------------------------*/
#Header ul{
	display: block;
	position: absolute;
	top: 35px;
	margin: 0px;
	right:0px;
	left: 450px;
	padding: 0px;
	height: 44px;
	border-bottom: 0px solid;
	color: #eee;
	list-style-type: none;
	vertical-align: bottom;
	text-align: left;
	padding-left: 0px;
	white-space: nowrap;
	font-weight: normal;
	font-size: 15px;
	background-color: transparent;
	font-family: 'Roboto', sans-serif;
	z-index: 1;
	  text-shadow: 0 1px 8px rgba(0,0,0,0.5);
}

#Header ul li{
	display: inline-block;
	margin: 0px;
	text-decoration: none;
	border-bottom: 0px;
	background-color: transparent;
}

#Header ul li.TabOn{
	z-index: 100;
	background: transparent;
	border: 0px solid;
	color: #eee;
	font-size: 15px;
	text-decoration: none;
}

#Header ul li.TabOn a{
	color: #eee;
	text-decoration: none;
	background: transparent;
	border-bottom: 2px solid #eee;
