}

.site-aside {
    top: 20px;
    left: 850px;
}

.profile-header .avatar::after {
    background: none;
}

.poll-footer-info .asked-container .avatar {
    width: 49px;
    height: 49px;
}

.poll-comments .p-comment-avatar img {
    background: url(http://i.imgur.com/IAMWLM7.jpg) top center/contain no-repeat;
}

.featured-poll-active:not(.body-home) #reply {
    top: inherit;
}

.poll-footer-info .asked-container::before {
    display: none;
}
}

