/****************************start**************************************/

body {
	background: white;
}
.project.done>.name>.content,
.project.doneAppearance>.name>.content {
	font-size: small;
}
#controlsLeft {
	background-color: #fff;
	border: 1px solid #ccc;
	border-radius: 0;
	/*box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.09);*/
	
	font-family: Calibri;
	letter-spacing: 1.6px;
	text-transform: uppercase;
}
#controlsLeft a {
	margin-bottom: 6px;
}
#controlsLeft hr,
#itemSelectionControls hr {
	border-color: #ccc;
	margin-top: 5px;
}
.selected>.notes>.content {
	font-size: 18px;
}
.noted>.notes {
	font-family: 'Catamaran';
}
.noted>.notes>.content:hover {
	/*background-color:rgba(247, 178, 41, 0.24);*/
	
	background-color: rgba(226, 243, 235, 1);
	border-radius: 3px;
}
/*rgb(235, 59, 99);*/

.noted>.notes>.content {
	font-size: 16px;
	color: #074867;
	padding: 0 5px;
	transition: background-color 0.5s ease-in-out;
	line-height: 25px;
	height: auto;
	font-weight: bold;
	letter-spacing: 0.04em;
	border-radius: 5px;
}
.noted.selected>.notes>.content {
	color: #BF360C;
}
#documentView {
	/*font-family: 'lato', serif;
	
    font-size: 15px;
    line-height: 22px;
	margin:0px;
	padding:0px;*/
	
	visibility: visible;
	font-family: "oxygen mono";
	font-style: normal;
	line-height: 1.10909;
	font-size: 20px;
}
#pageContainer .project.open.selected > .children > .project > .name {
	margin-top: 0.4em;
}
.bullet,
#bulletBucket .bulletBucketBullet {
	background-color: rgba(181, 147, 22, 0.3);
	background-image: none;
}
.bullet:before,
#bulletBucket .bulletBucketBullet:before {
	background-color: #4691CE;
	border-radius: 10px;
	content: '';
	display: block;
	height: 6px;
	left: 6px;
	position: relative;
	top: 6px;
	width: 6px;
}
.bullet:hover,
.project.task>.name>.bullet:hover,
.project.open>.name>.bullet:hover,
.project.addedShared>.name>.bullet:hover,
.page.showCompleted .project.halfOpenSearchMatchAncestorWhenCompletedVisible:not(.shared):not(.addedShared)>.name>.bullet:hover,
.page:not(.showCompleted) .project.halfOpenSearchMatchAncestorWhenCompletedHidden:not(.shared):not(.addedShared)>.name>.bullet:hover {
	background-color: hsl(212, 100%, 87%);
}
.children {
	border-left: 1px solid rgba(82, 112, 195, 0.45);
}
.content .contentMatch {
	background-color: initial;
	/*  border-bottom: 4px solid yellowgreen;*/
	
	padding-bottom: 2px;
	background-image: linear-gradient(to bottom, rgba(39, 243, 106, .1), rgba(39, 243, 106, .1));
}
.content .contentTag,
.nameAnimated .contentTag {
	/*color: #0091EA;*/
	
	transition: background 1s ease;
	/*font-family:'Open Sans';*/
}
.content .contentTag:hover {
	background-image: linear-gradient(to bottom, rgba(97, 255, 160, .8), rgba(97, 255, 160, .8));
	color: black;
	padding: 1px;
}
.content .contentTagText,
.nameAnimated .contentTagText {
	-webkit-font-smoothing: antialiased;
	text-decoration: none;
	border-bottom: 0.00005em solid rgba(128, 128, 128, 0.54);
}
