.highlighted {
	background-color: yellow;
}
.mainTreeRoot.project.open.parent .selected>.children>.project>.name>#controls #expandButton {
	top: 4px;
}
.page {
	border: none !important;
	-webkit-box-shadow: 0px 0px 88px 13px rgba(0, 0, 0, 0.17);
	-moz-box-shadow: 0px 0px 88px 13px rgba(0, 0, 0, 0.17);
}
.project.addedShared>.name>.bullet {
	background-color: hsl(212, 100%, 96%);
	box-shadow: 0 0 5px hsl(212, 100%, 66%);
}
.project.done>.name>.content,
.project.doneAppearance>.name>.content {
	letter-spacing: 1px;
}
/*.project.done>.name, .project.doneAppearance>.name, .selected .project.done .project>.name, .selected .project.doneAppearance .project>.name{
	color:inherit !important;
}*/

.project.open,
.project.open > .children > .project {
	margin-top: 2px;
}
.project.open.selected > .children > .project > .name .content {
	line-height: 30px;
}
/*.project.open.selected > .children > .project > .name a.bullet {
    margin-top: 4px;
}*/

.project.shared>.name>.bullet {
	background-color: #80CBC4;
}
.project.shared.open>.name>.bullet {
	border: 0.005em solid #80CBC4;
}
.project.addedShared>.name>.bullet {
	background-color: #F48FB1;
	border: 0.005em solid #F48FB1;
}
.project.addedShared.open>.name>.bullet {
	border: 0.005em solid #F48FB1;
}
.project.task>.name>.bullet,
.project.open>.name>.bullet,
.noHalo .bullet {
	background-color: transparent;
}
.saveButton,
.showCompletedButton,
#buttonBar,
#helpButton,
#header {
	border: none;
}
.saveButton.saveNow:hover,
