css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("colorhexa.com") {

html, body
{color: #919191 !important;
background: #111 !important;}
  
h1, h2, h3, h4, h5, h6, strong, .equal::after, .sep::after, .title, .value, .description, .color-description p, .simple-demo .demo p
{color: #919191 !important;
text-shadow: none !important;
border: none !important;}  
   
a
{color: #00adee !important;
text-shadow: none !important;}

a:visited 
{color: #00668c !important;} 
  
.tw
{color: #fff !important;
text-shadow: 0 1px 0 rgba(0,0,0,.2) !important;} 
  
.tb 
{color: #000 !important;
text-shadow: 0 1px 0 rgba(255,255,255,.2) !important;}  
  
#support, #header, #footer, .share-color ul, .share-color span
{display: none !important;}  
  
.main
{color: #919191 !important;
background: #1A1A1A !important;
border-top: none !important;
border-left: 1px solid #00adee !important;
border-right: 1px solid #00adee !important;
border-bottom: 1px solid #00adee !important;
border-radius: 0px !important;}  
  
.main-search
{color: #919191 !important;
background: #1A1A1A !important;
border-top: 1px solid #00adee !important; 
border-left: 1px solid #00adee !important;
border-right: 1px solid #00adee !important;
border-bottom: none !important;
border-radius: 0px !important;} 
  
.sp-replacer, .color-description, .demo, .demo strong
{color: #919191 !important;
background: #222 !important;
border: none !important;} 
  
#search-input
{color: #919191 !important;
background: #222 !important;
border: 1px solid #222 !important;} 
  
.scheme ul li pre, .t, .d, #hintbox ul
{color: #919191 !important;
background-color: #222 !important;
box-shadow: none !important;}
  
#search-submit
{color: #919191 !important;
background-color: #222 !important;
box-shadow: none !important;
background-image: url(http://www.colorhexa.com/static/i/search.min.svg), linear-gradient(to top,#222222,#222222) !important;}  
  
#hintbox li a:hover
{color: #919191 !important;
background-color: #333 !important;}  
  
code
{color: #00adee !important;
background: #333 !important;
border: 1px solid #00adee !important;
opacity: 1 !important;}   
  
.graph-wrapper .t  
{color: #919191 !important;
background: #333 !important;
text-decoration: none !important;
border-radius: 0px !important;}   
  
.color-image, .graph-wrapper, th.title, td.value, td.code, .color-list > tbody > tr > td:nth-child(2), .color-list > tbody > tr > td:nth-child(3), .color-list > tbody > tr > td:nth-child(4), .color-list > tbody > tr > td:nth-child(5), .color-list > tbody > tr > td:nth-child(6), .color-list > tbody > tr > td:nth-child(7), .color-list > tbody > tr > td:nth-child(8)
{background: #222 !important;
text-shadow: none !important;}  
  
#article, .color-blindness div:hover, .color-blindness li
{background: none !important;}
  
.bgtw  
{background: #222 !important;}  
  
.highlight
{background: #333 !important;}
  
.scheme ul li pre::before
{background-color: #222;}
  
#navigation a:hover 
{border: 1px solid #00adee !important;}  
  
#hintbox li span
{border-color: transparent transparent transparent #333 !important;}
  
.header 
{border-bottom: 1px solid #00adee !important;}
  
.table-conversion abbr 
{border-bottom: 1px dashed #919191 !important;}
  
#search
{padding: 0px !important;}
 
.g-top
{margin-bottom: 0px !important;}
  
.sp-preview-inner
{border-radius: 0px !important;} 
  
.schemes #complementary li, .schemes #analogous li, .schemes #split-complementary li, .schemes #triadic li, .schemes #tetradic li, .schemes #monochromatic li, .gradients .scheme li
{height: 70px !important;}

}
