css:
@-moz-document domain("accuweather.com") {
#header-davek,
#aadTop300,
#trending-now,
#ed-cal-content,
#aadBot300,
#offers,
#eventCrazyFrame
{display:none!important}

.settings-wrapper {
    margin: 6px auto;
    padding-bottom: 15px;
}

#feed-tabs.panel-list li.hv.cl {
    width: 155px !important;
}
body#forecast-extended #feed-tabs.panel-list li.current {
    width: 368px !important;
}
body#forecast-currently #feed-tabs.panel-list li.current {
    width: 523px !important;
}
#feed-tabs.panel-list li.cl {
    text-align: center;
}
#feed-tabs.panel-list li.cl .icon {
    margin: 10px auto;
}
.panel-list .bt-more {
    text-align: center;
    display: block;
    width: 134px;
}

#wrap-header-nav {
	box-shadow:none;
	border-top: 1px solid rgba(31,56,77,.5);
}

#radar {
    width: 302px;
    margin: auto;
}
#leaflet-map,
#leaflet-map #map_canvas{
	width:100% !important;
}
#feature-radar .inner a.wrap>div {
	width:100% !important;
}

.radar-key>table {
	width:95%;
}

.feature-radar{
    background-color: #222;
}
.feature-module .feature-radar .full-width-button {
	width:50% !important;
}

.sky-hourly+.hourly-table>table {
width:620px;
}

.column-1 {
	width:1004px;
}
.column-1, 
.column-2  {
float:none;
}
}
