@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
  
  html, body, #player, #player-legacy {
    background: #0b0b0b !important;
    color: white !important;
  }
  
  .yt-card {
    margin: 0 0 10px;
    border: 0;
    background: #0F0F0F;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.45);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: white !important;
  }
  
  #watch-header .yt-user-info a {
      display: inline-block;
      height: 22px;
      color: #FFF;
      font-weight: 500;
      max-width: 315px;
      overflow: hidden;
      text-overflow: ellipsis;
      vertical-align: top;
  }
  
  #watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover {
    background: none;
    border: none;
    color: white;
  }
  
  .yt-card .yt-uix-tabs button {
    margin-bottom: 15px;
    border-bottom: 1px solid #e2e2e2;
    color: white !important;
  }
  
  .comment-simplebox-arrow {
    height: 12px;
    position: absolute;
    top: 0;
    width: 12px;
    left: 0;
    margin: 0;
    display: none;
  }
  
  .yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
    border-color: #d3d3d3;
    background: #f8f8f8;
    color: #F5F5F5;
  }
  
  .comment-simplebox-frame {
    border: 1px solid #232323;
    border-radius: 2px;
    box-shadow: inset 0 0 1px rgba(0,0,0,.05);
    margin-left: 11px;
    margin-bottom: 8px;
    padding: 10px;
    min-height: 28px;
  }
  
  .comment-simplebox-text, .comment-simplebox-prompt {
      background-color: rgba(26, 26, 26, 0);
      color: #FFF;
      line-height: 16px;
      text-align: left;
  }
  
  .yt-badge {
    border: 1px solid #cc181e;
    padding: 0 4px;
    height: 13px;
    color: #CC181E;
    font-size: 11px;
    font-weight: normal;
    text-transform: uppercase;
    text-decoration: none;
    line-height: 13px;
    display: inline-block;
  }
  
  .video-list .video-list-item .title {
    color: #FFF;
    font-size: 13px;
    font-weight: 500;
  }
  
  #watch-description {
    color: #DDD;
    line-height: 17px;
    overflow: hidden;
    position: relative;
    z-index: 1;
  }
  
  .comment-simplebox {
    background-color: rgba(0, 0, 0, 0);
    overflow: hidden;
    display: none;
    position: relative;
  }
  
  .comment-simplebox-renderer-collapsed-content {
    border: 1px solid #0f0f0f;
    color: #b8b8b8;
    cursor: pointer;
    margin-left: 11px;
    min-height: 28px;
    border-radius: 2px;
    padding: 10px;
  }
  
  #browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
