    color: #F5F5F5;
  }
  
  .yt-uix-button-default:hover, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
