    line-height: 29px;
    border-bottom: 3px solid;
    border-color: #cc181e;
    padding-bottom: 0;
    display: inline-block;
    color: #bc1312;
  }
  
  a.yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item {
