    padding-bottom: 10px;
    border-bottom: 1px solid #0F0F0F;
  }

  .feed-item-container.legacy-style .feed-item-main {
    border-bottom: 1px solid #0f0f0f;
  }
  
  .feed-item-container .feed-item-main {
    color: #DEDEDE;
    font-size: 12px;
  }
  
  #watch8-action-buttons {
    position: relative;
    padding-top: 5px;
    border-top: 1px solid #0f0f0f;
  }
  
  .yt-card .yt-uix-button-expander:hover {
