@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kilux.de") {
  @media screen and (max-width: 1024px) {
    .content a {
      line-height: 1.7em;
    }
    div#Seite{
      min-width: auto;
      width: 100%;
    }
    .content {
    min-width: 480px;
    }
    table.programm {
      min-width: 800px;
    }
    .content > table:nth-child(46),
    .content > table:nth-child(57){
      max-width: 550px;
    }
  }
}
