css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(tabun.everypony.ru) {
body {
	background: none;
}
ol>li>span{float:right;}
ol>li>span>span{width:80px;display: inline-block;}
ol>li>img+a+span{display:none;}
ol>li>img+a>s{font-weight:normal;}
 
.comment-content {
	border-radius: 0;
    border: 0;
    background: none;
    margin: 0;
    padding-bottom: 2px;
}
div.comment-wrapper>div.comment-wrapper {
	border-left: 1px dotted #ccc;
	margin-left: -1px;
}
.comment .folding {
	top: 0;
 	background: none;
    width: 12px;
    height: 12px;
    border: 0;
}

section.comment {
	border-left: none;
}
#container {
	width: 100%;
	max-width: 100%;
	min-width: 1280px;
	background: none;
	background: #fff;
	border-radius: 0;
	margin-bottom: 0;
}
#header {
        background-color: #fff;
	background: url('https://cdn.everypony.ru/static/9c454ac63b03837891f1/img/b630.jpg') repeat-x;
	padding: 0;
	padding-left: 1%;
	border-bottom: 1px solid #ccc;
	min-height: 60px;
	height: 6%;
	width: 74%;
	position: fixed;
	top: 0;
	left: -1px;
	z-index: 99;
}
.widemode #container #header {
    width: 100%;
}
.widemode #content-wrapper {
 	width: 97%!important;  
    float: left;
}
.comment-info {
 	margin-top: 2px;   
}
#nav-main, #nav-main li {
	height: 44px;
	line-height: 30px;
}
#wrapper {
	padding-top: 0;
	padding: 0;
	min-width: 1280px;
}
#content-wrapper {
	padding-top: 65px;
	padding-left: 3%;
	min-width: 861px;
}
.block.block-type-stream, .block.block-type-blogs {
	background: none;
}
#is-single-article {
	position: fixed;
	top: 30px;
	left: 15px;
	z-index: 100;
}
#footer {
	margin-bottom: 0;
    margin-left: 35px;
    width: auto;
}
.block {
	border-radius: 0;
	border-bottom: 1px solid #dce1e6;
	background: none;
	margin: 0;
}
.block-type-tags, .donate, #down-switch, .running-ponies,#c-header, .block-type-search, .toolbar-topic, .nav.nav-menu, .dropdown-user-menu, .version {
	display: none;
	height: 0px;
}
#dropdown-user-menu {
	display: block;
}
#header .dropdown-user .avatar {
	height: 44px!important;
	width: 44px!important;
	margin: 0 -54px 0 0;
}
.auth {
 	margin-right: 10px;   
}
#sidebar {
	position: fixed;
	top: 0;
	right: 0;
	height: 95%; 
	overflow: auto;
	background: url('https://cdn.everypony.ru/static/9c454ac63b03837891f1/img/b630.jpg') repeat-x #fff;
	border-left: 1px solid #dce1e6!important; 
}
#content .nav.nav-pills {
	z-index: 100;
	margin-top: 0;
	position: fixed;
	top: 35px;
	left: 1%;
	width: 50%;
	padding-top: 2px;
}
a {
	color: #104BA9;
}
.topic-title a {
	text-decoration: none;
}
.profile-photo-wrapper {
	margin: 10px 1%;
	text-align: center;
}
#widemode {
	width: 25%;
	height: 5%;
	min-height: 50px;
	margin: 0;
	padding: 0;
	border-radius: 0;
	border-right: none;
	background-color: #fff;
}
#despoil, #widemode-switch {
	display: block;
	float: right;
	height: 70%;
	padding-top: 3%;
	width: 20%;
	text-align: center;
	margin-right: 0!important;
	background-color: #fff;
}
#despoil:hover, #widemode-switch:hover {
    background-color: #dce1e6;
    cursor: pointer;
}
/*#up-switch {
	background: none; 
	background-color: #fff;
	width: 20%;
	float: left;
	margin: 0;
	padding: 0;
	height: 70%;
	padding-top: 3%;
	bottom: 0;
}*/
#up-switch, #up-switch:hover {
	
	background-position: center;
	background-repeat: no-repeat;
    background-size: 90%;
	margin-right: 0!important;
    position: fixed;
    top: 61px;
    left: 0;
    height: 100%;
    width: 2%;
    background-color: #dce1e6;
    opacity: 0.3;
}
#up-switch:hover, #despoil:hover {
	 opacity: 1;
}
#widemode a {
	margin: 0;
	color: #333;
	text-decoration: none;
}
#widemode button.button-small {
	background-color: #fff;
	border: 0;
	box-shadow: none;
	border-radius: 0;
	width: 20%;
	float: left;
	margin: 0;
	height: 100%;
	padding: 0; 
}
#widemode button.button-small:hover {
	background: #dce1e6;
}
.tabun_fixes {
	display: block;
	float: left;
	position: relative;
	width: 20%;
	height: 100%;
	background-color: #fff!important;
}
.tabun_fixes:hover {
	background-color: #dce1e6!important;
}
}
