css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anonib.com") {

/* MISC */
/* Made by HofR 2008 */

/* Cascading Style Sheet */
div center {
    display: none;
}
div[style$="black;"] {
    border: 0px !important;
}
div[style$="black;"] center {
    display: block
}
td[width="140"],
td[width="140"] + td {
display: none;
}

hr {
    border: 1px dotted #999999
}
.firstimg {
    margin-right: 6px !important;
}
html,
body,
#boardbg {
    background: #E1E1DB !important;
    font-size: 13px;
}
.boxy {
    background: transparent !important;
    border: 0 !important;
}
.bigboxy,
.imgboxy,
.greenboxy,
#text {
    border: 1px solid #AAA !important;
    background: #cccdc3 !important;
}
#text {
	-moz-border-radius-topleft: 0px !important;
}
.cutrep,
.cutthreadpost {
    width: 650px !important;
}
#indexbox {
    display: inline !important;
    margin: 5px !important;
}
.hideThreadIcon {
    padding:6px;
    float: right;
    font-family: "Courier New", Courier, monospace;
}

#threadpostarea {
    border: 1px solid #d1d1c9 !important;
    background: #d1d1c9 !important;
    -moz-border-radius-topleft: 8px;
    -moz-border-radius-topright: 8px;
}
#postarea {
    border: 1px solid #d1d1c9 !important;
    -moz-border-radius-bottomleft: 8px;
    -moz-border-radius-bottomright: 8px;
    height: 230px !important
}
#subarea,
#returnbar {
    background: #cccdc3 !important;
    -moz-border-radius-bottomleft: 8px;
    -moz-border-radius-bottomright: 8px;
    border: 0 !important;
    font-size: 14px;
    text-align: center
}
#subarea {
    display: block !important;
    margin-bottom: 12px
}

#indexbox,
#linkShowHide {
    background: #E1E1DB !important;
    border: 0 !important;
}

#boxy {
    border: 0 !important;
    font-size: 13px
}

/* BOARDS */

body[class^="bodyBoard"] > div > div {
    -moz-border-radius:0px;
}

body > div > div,
.replycopy {
    background: #cccdc3 !important;
    -moz-border-radius:5px;
    padding: 4px !important;
    font-size: 12px
}

body > div > div[id^="t"] {
	-moz-border-radius:5px !important;
}

#subarea {
    display: block !important;
    margin-bottom: 12px
}

body > div > div:after {
    content: "";
    display: block;
    height: 4px;
    clear: both;
}

/* IMG LINKS */
.imgbox a:link img,
.imgbox a:visited img,
.firstimg a:link img,
.firstimg a:visited img {
    -moz-border-radius:2px;
    border: 4px solid #888 !important;
    padding: 0px;
    margin-top:4px;
}
.imgbox a:hover img,
.imgbox a:visited:hover img,
.firstimg a:hover img,
.firstimg a:visited:hover img {
    border: 4px solid #62A8AF !important;
    padding: 0px;
    margin-top:4px;
}

/* BODY / TEXT / LINK */
body {
    font-family: Arial, Verdana, Helvetica;
    background: #E1E1DB !important;
    color: #232323
}
.filesizemain {
    font-family: "Courier New", Courier, monospace;
    font-size: 12px
}

.reply,
.theText,
.imgboxy,
.bigboxy,
.greenboxy,
.rules {
    font-size: 12px;
}
#treplytext {
    font-size: 12px;
    padding-bottom: 4px;
}
a:link,
a:visited {
    color:#006699 !important;
    text-decoration: none;
}
#boxy a:hover,
#boxy a:visited:hover,
a:hover,
a:visited:hover {
    color:#D1213C !important;
}
#boxy a:hover,
#boxy a:visited:hover {
    background: transparent !important;
}
.postername {
color: #4B8F08 !important
}
.threadtitle {
color: #555 !important
}

/* NEW COLOR */
#indexbox,
#returnbar,
#postarea,
#threadpostarea,
#threadpostareatop,
.reply,
#nothread,
#linkShowHide {
    background: #d1d1c9 !important;
}
form .postinput .cbox {
    background-color:#EFEFEF  !important;
}
form .postblock,
td.postinput,
form .postblock {
    background:#EFEFEF  !important;
    border: 1px solid #EFEFEF  !important
}
form input, form textarea, form select {
    background: #CCCCCC !important;
    color: #333 !important;
    border: 1px solid #666666 !important;
}

/* END */
}
