css:
@-moz-document domain("forum.wakfu.com") {
body{
    background-color: rgb(40,40,40);
}

#page #submenu_right {
    background: rgb(60,60,60);
    height: 42px;
}

#postlist_table{
    background-color: rgb(60,60,60);
    border: rgb(50,50,50);
}

#page #headersearch {
    background: rgb(60,60,60);
}

#page #headersearch a {
    color: #36A5AD;
}

#page #headersearch form input#ak-tl-search-box {
    border-top: 1px solid rgb(80,80,80)!important;
    border-left: 1px solid rgb(80,80,80)!important;
    border-bottom: 1px solid rgb(80,80,80)!important;
    border-right: none!important;
    color: rgb(130,130,130);
}

#page #headersearch form input[type="submit"] {
    border-top: 1px solid rgb(80,80,80);
    border-right: 1px solid rgb(80,80,80);
    border-bottom: 1px solid rgb(80,80,80);
}

.aktable th.bigtitle, .aktable th, .borderwrap .aktable th, #topiclist_table tr td.darkrow1 {
    color: #7e7e7e;
    background: rgb(60,60,60) top left repeat-x!important;
}

.aktable th.bigtitle a, #topiclist_table tr td.darkrow1 b {
    color: #7e7e7e;
    padding-left: 25px;
    background: none top left no-repeat!important;
}

.aktable tr td.row1, .aktable tr td.row2 {
    color: rgb(130,130,130);
    background: rgb(80,80,80);
    border: 1px solid rgb(80,80,80);
}

div.maintitle {
    border: none;
    border-bottom: 1px solid rgb(80,80,80);
}

#content_large div.postlist_head, div.maintitle, td.maintitle {
    background: none no-repeat top left rgb(60,60,60)!important;
    padding: 2px 8px 0 40px;
    height: 40px;
}

#toptopicslist th+th {
    border-left: none;
}

#toptopicslist th {
    border-right: 1px solid rgb(60,60,60);
    border-bottom: 1px solid rgb(80,80,80);
}

#toptopicslist th {
    background-color: rgb(60,60,60)!important;
    color: rgb(130,130,130);
    font-weight: bold;
}

#toptopicslist th {
    text-align: left;
    border-left: 1px solid rgb(60,60,60);
    padding-left: 5px;
    font-weight: normal;
}

.topicrow{
    border-left: 1px solid rgb(80,80,80);
}

#topiclist_table tr td.darkrow1 {
    border-right: 1px solid rgb(60,60,60);
}

.bottom_toolbar {
    border-top: 1px solid rgb(60,60,60);
    background: rgb(60,60,60);
    border-left: 1px solid rgb(60,60,60);
    border-right: 1px solid rgb(60,60,60);
    border-bottom: 1px solid rgb(60,60,60);
}

.addreply .centerbg {
    border: none;
}

.btn_top {
    border: none;
}

/*Lien vers les différentes pages - [1] [2] etc...*/
.minipagelink, .minipagelinklast {
    background: none repeat scroll 0 0 rgb(60,60,60)!important;
    border: 1px solid rgb(60,60,60)!important;
    box-shadow: none;
}

.minipagelink a {
    color: rgb(130,130,130);
}

input {
    background-color: rgb(80,80,80);
    border-color: rgb(80,80,80);
    border-style: none;
}

keygen, select, select[size="0"], select[size="1"] {
    border-color: rgb(80,80,80);
}

select {
    color: rgb(140,140,140);
    background-color: rgb(80,80,80);
    border-color: rgb(80,80,80);
}

#submenu_flags label {
    color: rgb(130,130,130);
}

.btn_green2 .centerbg {
    color: rgb(90,90,90)!important;
}

.btn_blue .centerbg {
    color: rgb(90,90,90)!important;
}

.btn_top .centerbg {
    color: rgb(50,50,50);
}

.addreply .centerbg {
    color: rgb(170,170,170)!important;
}

.form_search .btn_grey .centerbg {
    color: rgb(190,190,190);
}

.btn_grey .centerbg {
    background: none top left repeat-x #ff4800!important;
}

.btn_grey .leftbg {
    background: none top left repeat-x #ff4800!important;
    height: 21px;
    width: 4px;
}

.btn_grey .rightbg {
    margin-right: 5px;
    background: none top left repeat-x #ff4800!important;
    height: 21px;
    width: 4px;
}

#page .maintitle a {
    color: rgb(190,190,190);
}

.postblockdate {
    background: none repeat scroll left top rgb(60,60,60)!important;
}

/*background case où on poste*/
.post1.post_main_container, .post2.post_main_container {
    background: none repeat-x scroll 0 bottom rgb(90,90,90)!important;
    border-right: none;
    color: rgb(190,190,190);
}

.postcolor a:link, .postcolor a:visited, .postcolor a:active {
    text-decoration: underline!important;
    color: #1a9aa5;
}

#akwrapper .maintitle a.btn_reply, a.btn_reply {
    margin-left : 2px;
    border: none!important;
    background: #51BE1F!important;
    color: rgb(50,50,50);
}

.quotemain, p.quote {
    background: rgb(140,140,140);
    border: 1px solid rgb(75,75,75);
    color: #005d5b;
}

.quotetop {
    background: rgb(120,120,120);
    border: 1px solid rgb(75,75,75);
    border-bottom: 1px solid rgb(75,75,75);
    color: #005d5b;
}
}
