css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.facebook.com") {

  html, body {background-color: #dddfe2}
  #mbasic_inline_feed_composer {display:none;}
  #viewport {max-width:none;
  -webkit-column-count: 5;
     -moz-column-count: 5;
          column-count: 5;
  -webkit-column-width: 304px;
     -moz-column-width: 304px;
          column-width: 304px;
    -webkit-column-gap: 1px;
       -moz-column-gap: 1px;
            column-gap: 1px;
  }
  .e #viewport {max-width:none;  margin-top:20px;margin-bottom: 20px; }  

  div[id^="u_0"] {overflow: auto;padding:10px;}
  div[id^="u_0"] div table:hover  {background: #eee !important;} 
  a[id^="u_0"] > div:hover  {background: #eee !important;} 
  

  div	a[href*="stories.php?aftercursorr"] 
  {display:block;position:absolute;top:82px;left:-15px;padding:5px 25px 5px 25px;}
  div, span, p, h3, label {font-size: 9px !important;line-height:20px;color:black;}
  [placeholder="Search Facebook"] 
  {font-size: 9px !important;padding-left:10px !important;font-style: italic;}
  #objects_container {margin-top:40px;}
  a[href*="language.php?"], #search_div {display:none;} 
  a[href*="add_friend.php"] span {color: #fff !important;}
  a[href*="add_friend.php"]:hover span {color: #000 !important;}
  img[height="13"], img[height="12"], img[height="14"] {position:relative;top:4px;}
  img[height="16"] {padding-right:5px;padding-left:5px;}
  img[height="16"]+span {color: #000 !important;position:relative;top:-2px;}
  
  #m_story_permalink_view div div[id^="u_0"] 
  {padding:15px;border-width : 25px 0px 0px 0px;position:relative;top:-40px;}
  div[id^="ufi_"] {Font-size:9px;} 
  div[id^="comment_replies_more_"] {padding-bottom:20px;}
  #m_story_permalink_view h3 {padding: 10px 18px 0px 18px;} 
  #m_story_permalink_view h3+div {padding: 0px 18px 18px 18px;}
  #m_story_permalink_view h3+div+div {padding: 0px 2px 2px 18px;}

  a:link  {font-size:9px;padding:5px;}
  a:hover {background:#eee !important; background-color:#eee !important;color:blue !important;} 

  input[value="More"], input[value="Add Photo"] {position:relative;top:+4px;}
}
