css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("maps.secondlife.com") {

html, body, #header, #map-header, #map-container, div.gmnoprint:nth-child(1) > div:nth-child(1), div.gmnoprint:nth-child(1) > div:nth-child(1) > div:nth-child(2)
{color: #919191 !important;
background: #1A1A1A !important;
box-shadow: none !important;}
  
h1, h2, h3, h4, h5, h6, .balloon-content, #search-input-wrapper input
{color: #919191 !important;}    
  
a
{color: #00adee !important;}

a:visited 
{color: #00668c !important;} 
  
#search-container, .slurl-info, .gm-style > div:nth-child(2), .gmnoprint.gm-style-cc, .gm-style-cc, li.separator, .balloon-content img
{display: none !important;}
  
#SearchContainer, .gm-style > div:nth-child(1) > div:nth-child(4) > div:nth-child(4) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4)  
{color: #919191 !important;
background: #222 !important;
box-shadow: none !important;
border: 1px solid #00adee !important;} 
  
button, .balloon-content .buttons a
{color: #919191 !important;
background: #333 !important;}    
 
.dropdown_list > li:nth-child(1) > a:nth-child(1)
{color: #919191 !important;
background: #333 !important;
border-top: 0px solid #00adee !important;
border-right: 1px solid #00adee !important;
border-left: 1px solid #00adee !important;
border-bottom: 0px solid #00adee !important;}  
  
.dropdown_list > li:nth-child(2) > a:nth-child(1)
{color: #919191 !important;
background: #333 !important;
border-top: 0px solid #00adee !important;
border-right: 1px solid #00adee !important;
border-left: 1px solid #00adee !important;
border-bottom: 1px solid #00adee !important;}   
  
  
.gm-style > div:nth-child(1) > div:nth-child(4) > div:nth-child(4) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(1) > div:nth-child(1), .gm-style > div:nth-child(1) > div:nth-child(4) > div:nth-child(4) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(1)  
{background: #222 !important;}   
  
#searchcontrols a.dropdown 
{color: #919191 !important;
background: url("http://maps-lecs.lindenlab.com/agni/25/_img/dropdown-arrow.png") 135px 13px no-repeat #333 !important;}    
   
a.dropdown 
{border-left: 1px solid #00adee !important;}  
    
#map-header 
{border-bottom: 3px solid #333 !important;} 

#map-container > div 
{box-shadow: none !important;}  

.map-search-opened 
{padding-left: 0px;}  
  
#map-container
{padding-top: 110px !important;}
  
ul.dropdown_list
{top: 59px !important;}
  
}
