/* Hey there! If you are editing this code for what ever reason, have fun c: I have not documented what anything does so tinkering may take some time. Sorry. I should mention that there are a lot of redundencies. This code needs a good scrubbin! If you have any questions, poke TeyoOtter on furvilla. Yes! you are free to edit and use this code however you wish!*/
@-moz-document domain("furvilla.com") {
body {
    background: #00001a;  
}
.align-center {
    color:#bfbfbf;
}
.content {
    background: none;
    color: #bfbfbf;
}
.user-panel {
    background:none!important;
    color: #bfbfbf;
}
.user-panel a {
    color: #bfbfbf!important;
}
.user-info h4 {
    color: #bfbfbf;
}
header nav {
    background-image: linear-gradient(rgba(0,50,105,0.2), #00001a);
}
header nav a {
    color: #bfbfbf;
}
.fa {
    color: #bfbfbf!important;
}
.content h1 {
    color: #bfbfbf;
}
.content h1 small {
    color: #bfbfbf;
}
.content h2 {
    color: #bfbfbf;
}
.content h3 {
    color:#bfbfbf;
}
.counter {
    border-color:#bfbfbf;
}
.widget-content {
  background-color: rgba(0,50,105,0.2); 
	border:  none;
	border-radius: 20px;
        color: #bfbfbf;
        font-family: Verdana, Geneva, sans-serif;
}
.widget-content, .jobs > li > a {
    background-color:rgba(0,50,105,0.2);
    border-color: #bfbfbf;
}
.widget-content, .jobs > li > a > span {
    color:#bfbfbf !important;
    border-color:#bfbfbf;
}
.widget-content, .jobs > li > a:hover {
    border-color:#00009f;
}
.widget-content .stats > p {
    color:#bfbfbf;
}
.widget-header h3 {
    background:none;
    color: #bfbfbf;
    text-align:center;
}
.widget-header {
    background:none;
    border:none;
}
.pull-right.btn.disabled {
    background:none;
    color:#0000fb !important;
}
.btn {
    background-image: linear-gradient(#00006f, #00004a);
}
a {
    color: #bfbfbf!important;
}
#logo a {
    opacity:0.4;
}
.btn-link {
    color: #bfbfbf !important;
}
.search {
    background: #00001a;
    color:#bfbfbf;
}
.well.poison {
    background-color:rgba(0,50,105,0.2);
}
.help-block {
    background:none;
    color: #bfbfbf;
}
.alert.success.margin-10px {
    background:#00b300;
    color:#0f0033;
}
.alert.error.margin-10px {
    background:#f00;
    color:#000;
}
.top {
    background: #00001a;
}
.sod_select:hover,
.sod_select.focus {
    border-color: #bfbfbf!important;
}
.sod_select,
.sod_select > span {
    background-color:#001133 !important;
    color:#bfbfbf;
}
.toolbar-title h2,
.toolbar-title h3 {
    color: #bfbfbf;
}
strong.blue {
    color: #bfbfbf;
}
label {
    color:#bfbfbf;
}
.paintie-gallery .paintie.text-center {
    background-color:rgba(0,50,105,0.2) !important;
    color:#bfbfbf;
}
.paintie-gallery .paintie.text-center:hover {
    border-color:#00009f;
}
.inventory-item-info,
.item-block {
    border: 1px solid #bfbfbf;
    background-color:rgba(0,50,105,0.2);
}
.inventory-item-info .counter,
.item-block .counter {
    border:none;
    color: #bfbfbf;
}
.inventory-item-info .name,
.item-block .name {
    color:#bfbfbf;
}
.inventory-item-info:hover {
 border-color:  #00009f;
}
.select2-dropdown.select2-dropdown--below,
.select2-search__field {
 background-color:#001133 !important;   
    color:#bfbfbf;
}
.header-right-toolbar-box {
    background:none;
    color:#bfbfbf;
    border:none;
}
.registration-well {
    background:none;
}
.registration-well .input {
    background:#001133;
    color:#bfbfbf;
}
.registration-well > label {
    color:#bfbfbf;
}
.inventory .inventory-item .crafting-list-item-info {
    background:rgba(0,50,105,0.2);
}
.inventory .inventory-item .crafting-list-item-info:hover {
    border-color: #00009f;
}
.inventory .inventory-item .crafting-list-item-info > .status {
    color:#bfbfbf;
}
.pagination > li > a,
.pagination > li > span {
    color: #bfbfbf !important;
    background-color:rgba(0,50,105,0.2);
    border: 1px solid #bfbfbf;
}
.pagination > .active > a,
.pagination > .active > a:hover,
.pagination > .active > a:focus,
.pagination > .active > span,
.pagination > .active > span:hover,
.pagination > .active > span:focus {
    color: #bfbfbf;
    background-color: #00004b;
    border-color: #bfbfbf;
}
.fur-residents-item-info {
    border: 1px solid #bfbfbf;
    background: rgba(0,50,105,0.2);
    color:#bfbfbf;
}
.fur-residents-item-info:hover {
 border: 2px solid #00009b;   
}
.well {
    background-color:rgba(0,50,105,0.2);
    color:#bfbfbf;
}
.fur-residents-item-info .status, .name {
 color:#bfbfbf !important;   
}
.fur-residents-item-actions.active {
    background-color:rgba(0,50,105,0.8);
    color:#bfbfbf;
}
.thread-user-info {
    background: rgba(0,50,105,0.2);
    color: #bfbfbf;
}
.thread-user-info a {
 color:#bfbfbf !important;   
}
.textarea {
    background-color: #00001a;
    color:#bfbfbf;
}
.content .left-column {
    color:#bfbfbf;
}
.control-label.col-xs-4 {
    color:#bfbfbf !important;
}
.table thead,
.table tr:hover .table thead {
    background-color:rgba(0,50,105,0.2) !important;
    color: #bfbfbf;
}
.table tbody,
.table td, .table td > a {
    background-color:rgba(0,50,105,0.2) !important;
    color:#bfbfbf !important;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus {
    color: #bfbfbf !important;
    background-color: #00009b;
}
.nav-pills > li > a {
 color:#bfbfbf !important;   
}
.modal-content {
    background-color: #00001a;
    color:#bfbfbf !important;
}
.modal-content h2,
.modal-content label {
 color:#bfbfbf !important;   
}
.modal-content .modal-body > p,
.modal.fade.in input,
.modal.fade.in select {
    background-color: rgba(0,50,105,0.2);
    color: #bfbfbf;
}
.panel.panel-default {
    background-color:rgba(0,50,105,0.2);
}
.panel.panel-default p {
    color:#bfbfbf;
}
.animal-item-info,
.animal-item-info .durability {
    background: rgba(0,50,105,0.2);
}
.animal-item-actions.active {
    background-color:rgba(0,50,105,0.6);
}
.animal-item-actions.active a:hover {
 background-color: rgba(0,50,105,1);   
}
.animal-item-info .status, .name {
    color: #bfbfbf !important;
}
.animal-item-info:hover {
    border-color:#00009f;
}
.villager-name a {
color:#bfbfbf !important;
}
.explore-item {
background: rgba(0,50,105,0.2);
border: #bfbfbf;
}
.crafting-list-item-info {
    background-color:rgba(0,50,105,0.2);
}
.crafting-list-item-info .status {
    color:#bfbfbf;
}
.crafting-list-item-info:hover {
    border-color:#00009f;
}
.form-control {
    background:#001133;
    color:#bfbfbf;
}
.construction-item-info {
    background-color:rgba(0,50,105,0.2);
}
.construction-item-info .status {
    color:#bfbfbf;
}
.construction-item-info:hover {
    border-color:#00009f;
}
.warrior-view-sword .color-thief-bg, .warrior-view-shield .color-thief-bg {
border: none;
background-color: rgba(0,50,105,0.2);
}
.warrior-view-sword:before, .warrior-view-shield:before {
background: none;
}
.warrior-buttons.countdown > a,
.btn.protection-quotient {
    background-color:rgba(0,50,105,0.2) !important;
    border-color:#bfbfbf !important;
    color:#bfbfbf !important;
}
.warrior-buttons.countdown > .btn.countdown-wait.disabled {
    background:#00001a;
    color:#bfbfbf;
}
.warrior-info-section {
    color:#bfbfbf;
}
.villager-data-wrapper {
background: none;
    color:#bfbfbf;
}
.progress-bar.progress-bar-mini.tooltipster.tooltipstered,
.progress-bar.progress-bar-mini.margin-10px,
.progress-bar.progress-bar-mini {
    background:#001133;
}
.progress-bar.progress-bar-mini.tooltipster.tooltipstered > .progress,
.progress-bar.progress-bar-mini > .progress,
.progress-bar > .progress,
.progress-bar.progress-bar-mini.margin-10px > .progress {
    background-image: linear-gradient(#00009f, #00004a);
}
.comboslider,
.comboslider-header,
.comboslider-prev,
.comboslider-next {
    background-color:#001133;
    color:#bfbfbf;
}
/*.comboslider-prev:before,
.comboslider-next:before {
    border-color:#bfbfbf;
} this is the arrows to costume emporium but they appear as squares. left it out because arrows look better.*/
.registration-well .select2-selection.select2-selection--single,
.registration-well  span,
.registration-well  .dropdown-wrapper,
.registration-well .select2-selection__rendered,
.registration-well .alert {
    background-color:#001133 !important;
    color:#bfbfbf !important;
}
.float-left {
color: #bfbfbf!important;
}
.job-description,
.job-description .job-text > p {
	background:none;
    color:#bfbfbf;
}
.well.poison,
.registration-well.poison {
    background-color: #660000 !important;
}
.well.text-center {
    background-color:rgba(0,50,105,0.2);
}
.well.text-center > p {
    color:#bfbfbf !important;
}
.fur-idol-prize {
    background-color:rgba(0,50,105,0.2);
}
.fur-idol-winners-heading.clearfix {
    background: none;
}
.forum .forum-category {
    background:rgba(0,50,105,0.2) !important;
    border: none !important;
    border-radius: 40px;
    text-align: center;
    /*font-family: Verdana, Geneva, sans-serif;*/
}
.forum,
.forum-header,
.forum-icon,
.forum-name,
.forum-posts,
.post-icon,
.post-subject.profanity-filter,
.post-subject,
.post-replies,
.post-last {
    background: none!important;
    border: none;
}

.thread-user-post,
.thread-user-post-top.clearfix,
.thread-user-post-middle.readable,
.thread-user-post-bottom.readable,
.profanity-filter.thread-post{
    background:#001133 !important;
    color:#bfbfbf !important;
}
.fvp-stall-price-tooltip {
    color:#bfbfbf;
}
.clearfix.margin-10px .gender-radio > label {
    background:rgba(0,50,105,0.2) !important;
}
.user-li {
    background:rgba(0,50,105,0.2)!important;
}
.chatroom.scrollable.ps-container.ps-active-y ul,
.chatroom-message-form input {
    background:rgba(0,50,105,0.2)!important;
    color:#bfbfbf !important;
}
.chatroom-wrapper.gradient::before {
    background:none;
}
.text-center .btn.disabled {
    background:none;
    animation:colordot 1s infinite;
}
@keyframes colordot {
   0% { color: #bfbfbf;}
   25% {color: #809fff;}
   50% {color: #ff8080;}
   75% {color: #99e699;}
   100% {color: #bfbfbf;}
}
.user-message.col-xs-10 {
    background-color:rgba(0,50,105,0.2);
}
.villager-data-minipet {
    background: none;
}
.input.price {
    background-color:rgba(0,50,105,0.2);
    color:#bfbfbf;
}
input#name.input {
 background-color:rgba(0,50,105,0.2);
    color:#bfbfbf;
}
.table.table-striped.table-hover > tbody > tr {
    background-color:rgba(0,50,105,0.2);
}
.dropdown-menu.multi-column.columns-2 {
 background-color: #001133;
}
.dropdown-menu.multi-column.columns-2 a:hover {
    background-color:rgba(0,50,105,0.2);
}
header nav .dropdown-menu a {
    color:#bfbfbf !important;
}
.mute {
    background:none !important;
}
.table-striped > tbody > tr:nth-of-type(odd) {
 background:none !important;   
}
.table tr:nth-child(odd) td {
    background:rgba(0,50,255,0.2) !important;
    
}
.alert.alert-danger {
 background-color:rgba(0,50,255,0.2);
    color:#bfbfbf;
}
.furcoins .bestseller::before {
 background-color:rgba(0,50,105,0.8) !important; 
}
.furcoins td {
 background-color: rgba(0,50,105,0.2) !important;
    color:#bfbfbf;
}

/*I need to practice taking notes >u< Below are edits on 12/28/2016 okay*/

.fur-residents-item-info .durability {         /*This line effects the villagers page. Makes the part where it says FC slot or house durb blue currently*/
    background-color:rgba(0,50,105,0.2);
}

}
