css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sarajevo-x.com") {

div#ads, iframe, div.banner234 {display:none !important;}

a.crni {color:#666666 !important;font-size:11px !important;font-weight:normal !important;text-decoration:none !important;}
a.crni:hover {color:#000000 !important;}

a.nav {color:#666666 !important;display:block !important;font-size:11px !important;font-weight:normal !important;padding:0 15px !important;text-decoration:none !important;}
a.nav:hover {color:#000000 !important;}

a.udarna {color:#009CD6 !important; text-decoration:none !important;}
a.udarna:hover {color:red !important; text-decoration:none !important;}

a.dobezs {color:#666666 !important;font-size:12px !important;font-weight:normal !important;text-decoration:none !important;}
a.dobezs:hover {color:#000000 !important;}

a.onaslov{color:#666666 !important;font-size:12px !important;font-weight:normal !important;text-decoration:none !important;}
a.onaslov:hover {color:#000000 !important;}
body, tr, td, input, select, textarea {font-family:Verdana,Helvetica,sans-serif !important;}

a.onad {color:#333333 !important;font-size:10px !important;font-weight:bold !important;letter-spacing:0 !important;text-decoration:none !important; border-bottom:1px red dotted !important;}

a.obezs {text-decoration:none !important;}

div.tekst {font-size:11px !important;line-height:16px !important;padding-bottom:20px !important;}

div.uvod {font-size:13px !important;font-weight:bold !important;line-height:18px !important;padding-bottom:20px !important;}

div#content {margin-top:20px !important;}

div.spacer {background-color:#DDDDDD !important;font-size:1px !important;height:1px !important;}

#vijesti-tabs li a {background-color:#F9F9F6 !important;border-bottom:1px solid #EDEBE4 !important;border-right:1px solid #EDEBE4 !important;color:#333333 !important;display:block !important;float:left !important;height:2em !important;line-height:2em !important;padding:8px 15px !important;text-align:center !important;text-decoration:none !important;
}
#vijesti-tabs li a.aktivan {background-color:#FFFFFF !important;border-bottom:1px solid white !important;color:#077ECB !important;font-weight:bold !important;text-decoration:none !important;
}
#vijesti-tabs li a.actives {background:#077ECB !important;border-bottom:1px solid #077ECB !important;color:#FFFFFF !important;font-weight:normal !important;letter-spacing:0 !important;padding:8px !important;text-decoration:none !important;
}
.komentar {display:none !important;}

}
