css:
@-moz-document url-prefix("https://bs.to/") {
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
  }
  * {
    font-family: Roboto !important;
    border: none !important;
  }
  html {
    background: url(https://wallpaperscraft.com/image/blue_shadow_stripes_light_15619_1920x1080.jpg);
    /*background: url(https://wallpaperscraft.com/image/scheme_lines_dark_white_47453_1920x1080.jpg);*/
    /*background: #0D0D0D;*/
  }
  body>#root {
    background: url(https://s.bs.to/img/header.png) no-repeat #212121;
    /*background: url(img/header.png) no-repeat #212121;*/
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    border-radius: 2px;
    color: #fff;
  }
  a {
    color: #0288D1;
    text-decoration: none;
  }
  a:hover {
    text-decoration: underline;                      
  }
  #user {
    background-color: #212121;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    border-radius: 2px;
    height: auto;
    padding: 20px;
    position: absolute;
    right: 0;
  }
  nav>ul,
  nav>ul>li>ul {
    background-color: #0288D1;
    line-height: 2em;
  }
  nav>ul>li {
    padding: .5em 0;
  }
  nav>ul>li:hover,
  nav>ul>li>ul>li:hover {
    background-color: #21a9f2;
  }
  nav>ul>li>ul {
    left: 0px;
    top: 3em;
    border-radius: 0 0 2px 2px;
  }
  nav>ul>li>ul>li {
    padding: .25em .5em;
  }
  nav>ul>li>ul>li:last-child {
    border-radius: 0 0 2px 2px;
  }
  nav>ul>li:last-child {
    padding: .5em;
  }
  #social-nav {
    display: none;
  }
  #sp_left>div>p {
    margin-bottom: 5px;
  }
  #sp_left>div>a {
    display: none !important;
  }
  ul.pages {
    padding: 5px;
  }
  .pages>li {
    width: 30px;
    line-height: 30px;
    border-radius: 2px;
  }
  .pages>li>a {
    color: rgba(255, 255, 255, .75);
  }
  .pages>li>a:hover {
    background-color: inherit;
    border-radius: 2px;
  }
  .serie .watched {
    background-color: #6FAC31 !important;
  }
  .pages>li:hover {
    background-color: #3c3c3c;
    -webkit-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 1);
  }
  .serie .watched:hover {
    background-color: #6FAC31 !important;
  }
  .serie .epiInfo {
    margin-left: 0px;
    background-color: inherit;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    border-radius: 0 2px 2px 2px;
    padding: .5em;
  }
  .epiInfo::before {
    content: ' ';
    position: absolute;
    background-color: inherit;
    width: 30px;
    height: 8px;
    top: -8px;
    left: 0px;
  }
  nav a {
    color: #fff;
  }
  #sp_right>small {
    display: none;
  }
  #sp_right {
    width: 180px;
  }
  #seriesdata table {
    margin: 0;
  }
  #seriesdata td ul li {
    border-radius: 0px;
  }
  #seriesdata td ul li:not(.noentry):hover {
    background: inherit;
    color: #0288D1;
  }
  tr:nth-child(even),
  .home #shoutbox dl>dt,
  .home li:nth-child(even) {
    background-color: rgba(255, 255, 255, .125);
  }
.home #shoutbox time, .home #news time {
    float: right;
    line-height: 20px;
}
    #sbMsg {
    color: white;
    background-color: #545454;

}
.admin {
    color: rgba(192, 57, 43,1.0);
    text-decoration: none;
}
.mod {
    color: rgba(41, 128, 185,1.0);
    text-decoration: none;
}
.cmod {
    color: rgba(142, 68, 173,1.0);
    text-decoration: none;
}
.smod {
    color: rgba(39, 174, 96,1.0);
    text-decoration: none;
}
.exadmin {
    color: rgba(127, 140, 141,1.0);
    text-decoration: none;
}
.tech {
    color: rgba(155, 89, 182,1.0);
    text-decoration: none;
}
.user {
    color: rgba(230, 126, 34,1.0);
    text-decoration: none;
}
  span.slink {
    display: none;
  }
  span.slink:first-child {
    display: inline;
    color: #0288D1;
    text-decoration: none;
    text-transform: uppercase;
    font-size: .75em;
  }
  footer {
    padding: 5px 0 10px 0;
    font-size: .825em;
  }
  #sp_left>table>tbody>tr {
    height: 2em;
  }
  tr.watched:nth-child(even) {
    background-color: #7abf36 !important;
  }
  tr>td>a {
    color: #fff;
  }
  #serInput {
    width: 100%;
    padding: .5em;
    box-sizing: border-box;
    height: 2em;
    background-color: transparent;
    color: #fff;
    outline: none;
    border: 1px solid white !important;
  }
  #serInput:active,
  #serInput:focus,
  #serInput:hover {
    border: 1px solid #0288D1 !important;
  }
  .random > h2:after {
    content: ' (KEIN DESIGN)';
    font-weight: normal;
    color: red;
  }
  .download-app {
    width: auto;
    font-size: 2em;
    font-weight: bold;
    margin: 0 0 10px 0;
    border-radius: 2px;
    background: #0288D1;
    color: #fff;
  }
  .download-app:hover {
    background: rgba(0, 0, 0, .125);
    color: #fff;
  }
  .home>#teaser,
  .home>.column>section {
    box-shadow: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    background-color: #0288D1;
    border-radius: 2px;
  }
  .home>#teaser>div,
  .home>.column>section>div {
    background-color: #3C3C3C;
  }
  .home section ul>li,
  .home section dl>dt,
  .home section dl>dd {
    padding: .25em;
  }
  .home section table>tbody>tr {
    height: 2em;
  }
  a[target="_blank"]{
    text-decoration: underline;
  }
#sbMsg {
    background-color: #545454;
    color: #ffffff;
}

#sbPosts {
    color: #ffffff;
}

fieldset label input {
    background-color: #545454;
    color: #ffffff;
}
}

@-moz-document regexp("https?://bs.to/serie/.*") {
a[target="_blank"]{
    padding-top: 65px;
    width: 250px;
  }
  a[target="_blank"] > img {
    display: none;
  }
  a[target="_blank"]{
    background: url(https://wlu16www46.webland.ch/assets/playbutton_blue.png) no-repeat;
  }
  .pages>.current,.pages>.current:hover {
    background-color: #0288D1;
  }
}
