css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeprodestiny.forumcommunity.net") {

  
  
  /*sfondo*/
  
  body {
  background: #081803 !important;
  }
  
  .mainheader .top {
    background: #1A3C0D !important;
   
  }
  
 
  /*header */
  
  .mainheader .center, .mainheader .bottom  {
    background: #1A3C0D !important;
  }
  
 
  .logo_apd {
    background: url(http://i.imgur.com/lvJbKaq.png) center center no-repeat !important;
}
  
  .mainheader .center > * > div {
    background: #072C0F !important;
  }
  
  .mainheader {
    color: #25D5C9 !important;
}
    
  element {
    background: rgb(0, 62, 5) none repeat scroll 0% 0% !important;
}
  
  
  
  /* login + chat */
  
  .sunbar {
    background: #875D14 !important;
    color: #C2F6FF;
}
  
  
  .list > *, table.mainbg tr, .profile .skin_tbl {
    background: #6F501A !important;
}
  
  .list > :nth-child(2n), table.mainbg tr:nth-child(2n) {
   background: #875D14 !important;
  
}
  .title, .darkbar {
    background: #875D14 !important;
   
}
  
  .reg, .tag, .board .skin_tbl, .sidebox {
    background-color: #e6b257 !important;
}
  
  body {
    color: #25D5C9 !important;
}
  .mhtag a {
    color: #25D5C9 !important;
   /* background-color: #6f501a !important; */
}
  

  
  
  /* topic */
  
  .big_list > * {
    background: #0F3E12 !important;
    box-shadow: inset 4px 0 0 #5C7149 !important; 
  }
  
  .big_list > :nth-child(2n) {
    background: #1A2F00 !important;
    box-shadow: inset 4px 0 0 #195620 !important; 
  }
  
  .forum .annuncio {
     background: #0F3E12 !important;
    box-shadow: inset 4px 0 0 #5C7149 !important; 
}

   .forum .annuncio > :nth-child(2n) {
    background: #1A2F00 !important; 
    box-shadow: inset 4px 0 0 #195620 !important; 
  }
  .forum .importante {
    background-color: rgb(15, 62, 18) !important; 
}
  
  .mtitle {
    font-weight: bold !important; 
    color: #000 !important; 
    background: none !important; 
}
  
  .nav a {
    color: #79CF29 !important; 
}
  
  
  /*num pag */
 
  .pages li a, .pages .current {
    color: #E4EA87 !important; 
    background: #247D13 !important; 
}
  
  .pages2 a:link, .pages2 a:visited {
    background: rgba(49, 186, 55, 0.7) !important; 
   
}
   .pages li a:hover {
    color: #E4EA87 !important; 
   background: #26B122 !important; 
}
  
  
  
  /* discussione post */
 
  .post_pos .title2.top {
    background-color: #104512 !important;
}
  
  .color {
    color: #4EFF00 !important; 
}
  .post {
    background: #1f300a !important; 
}
  .post .title2.top {
    background: #114B08 !important; 
}
  .post .title2.bottom {
    background: #083009 !important; 
}
  .quote, .code {  
    color: #87C028 !important; 
    background: #0D1E06 !important; 
}
  .quote .quote, .quote .code, .spoiler .quote, .code .code {
    color: #599617 !important; 
    background: #071206 !important; 
}
  
  .post .title2.top .lt a, .post .title2.top .when {
    color: #41A415 !important; 
}
  
  .post_neg .title2.top {
    background-color: #114B08 !important;
}
  
  .when {
    color: #2C9E6E !important; 
}
  .mods, .subs {
    color: #2C9E6E !important; 
}
  
  
  
  
  /* reply + form input */
  .skin_tbl {
    border-bottom: 4px solid #3d5f13 !important; 
    background: #0D1E06 !important;
    box-shadow: none !important; 
}
  .codebuttons, .forminput, .textinput {

    color: #60FF00 !important; 
    background: #000; 
   
  }
  .mback {
    background-color: #2f6b0e !important;
    border-bottom: 3px solid #50972a !important;
    border-radius: 4px !important;
}
  
  
  /* manga release */
  .manga_container {
    background: #7D9507 !important; 
    border: 2px solid #CF0 !important; 
}
  
 
  /* altro */
  
  /*search and bottom */
  
  .navsub .forminput {
    background: RGBA(18, 65, 23, 0.7) !important; 
  
  }
  
.navsub .forminput:hover {
    background: RGBA(53, 176, 29, 0.62) !important;
    color: white !important; 
}
  
  /*registrazione tab */
  element {
    border: 2px solid rgb(104, 119, 28) !important; 
    background: rgb(23, 72, 19) none repeat scroll 0% 0% !important; 
    color: rgb(61, 195, 48) !important; 
}

  
  
  /* statistiche glob */
  #board .stats .mback {
    background: #00B046 !important; 
    border-bottom: 3px solid #F0F0F0 !important; 
}
 #board .stats .list li {
    background: #0C3918 !important; 
}
  #board .stats .skin_tbl {
    background: #0D3B0D !important; 
}
  #board .stats .top .mback {
    border-color: black !important; 
    
}
  #board .stats .mback { 
    border-color: black !important; 
  }
  #board .stats .mtitle {
    color: #000 !important; 
}
  
  
  
}
