css:
@-moz-document domain("www.livesport.cz") {
body 


{background: url("http://1dut.com/wp-content/uploads/2011/07/30-black-themed-wallpapers-23.jpg"  /* "" */ ) no-repeat fixed center; 
    background-size: cover;
    /* line-height: 1.178; /*ROZTAZENOST NA VYSKU VSECH RADKU A BUNEK, TEDY I TEXT V NICH MA VIC MISTA KOLEM SEBE*/  
}

.adsenvelope iframe, .adsenvelope div {
    display: none !important ;
}
div.box_over_content, div.box_over_content div { 
    display: none !important ; 
}
.scrolling-mobile-version-wrap.static-position, .scrolling-banner-wrap.static-position {
    position: static !important ;
}


#fsbody > #fscon > .fs-table > .table-main tr.league.primary-top, .my-leagues .head, ul.menu.my-leagues li.head, .fs-table .odds-content tr.league.primary-top {
    font-size: 12.0px !important ; 
}

.fs-table .bold {
    /*font-family: "Arial Black" !important ; */
    color: #000 ;
    font-size: 13.0px ; 
}
.fs-table .playing, .fs-table .playing a {
    color: #c00 !important ;
}
.fs-table td span.padl {
    font-size: 12.0px ; color: #000;  
}
.fs-table td span.padr {
    font-size: 12.0px ; color: #000;
}





.fs-table .odds-content .sport-label, #local-mygames .sport-label, #search-results .sport-label {
    line-height: 11px;
    height: 11px;
    font-size: 11px;

}




.sport-icon {
    width: 26px;
    height: 14px;
    margin: 0 3px 0 0;
}



div.group-label span.group-label-name {
    /*v -Moje zapasy- jsou to pole "Skupina *" */
    background-color: #c00;
}





}

@-moz-document url-prefix("http://www.livesport.cz/zapas") {
body {
    background: white;
}
}
