css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("serebii.net"){

body {
  background: #262626 !important;
}
  
body table tbody tr td[background="http://www.serebii.net/BannerBg.jpg"] {
  background: url("http://i.imgur.com/p3Ushg5.jpg") 50% no-repeat #1a1a1a;
}
  
img[src*="http://www.serebii.net/Banner.jpg"] {
  width: 0px;
  height: 139px;
  padding-right: 90vw;
  background: url("http://i.imgur.com/k1Xd4x6.png") 50% no-repeat !important;
  background-size: 75vh !important;
}

p.info {
    border-bottom: 2px solid #0df !important;
  }
  
td[bgcolor="#507C36"] {
    background: #383838 !important;
  }

.art {
  background: #035 !important;
}
  
.footop {
  background: #49d !important;   
}
  
.curr {
  filter: hue-rotate(95deg) !important;
}
  
.foo {
  filter: hue-rotate(95deg) !important;
}
  
.footwo {
  filter: hue-rotate(95deg) !important;
}
  
.foodet {
  filter: hue-rotate(95deg) !important;
}
  
body center table tbody tr td:first-child + td {
   background: #383838;
  }
  
.tooltabcon {
    background: #49d !important;
  }
  
.tooltabhead {
    background: #27b !important;
  }
  
.alterdex { 
  background: #49d !important;
}

.fooevo {
    background: #49d !important;
  }  

.fooleft {
  background: #49d !important;    
}
  
font[color="#499fff"] {
    color: #adf;
}
  
form[name*="nav"] select {
  background: #fff !important;
}

form[name*="coast"] select {
  background: #fff !important;
}
  
form[name*="cent"] select {
  background: #fff !important;
}
  
form[name*="mount"] select {
  background: #fff !important;
}
  
form[name*="hoenn"] select {
  background: #fff !important;
}  

form[name*="atoz"] select {
  background: #fff !important;
}
  
form[name*="breed"] select {
  background: #fff !important;
}  
 
form[name*="colours"] select {
  background: #fff !important;
}
  
form[name*="evo"] select {
  background: #fff !important;
}
  
form[name*="pokeball"] select {
  background: #fff !important;
}
  
form[name*="recovery"] select {
  background: #fff !important;
}
  
form[name*="holditem"] select {
  background: #fff !important;
}  
  
form[name*="key"] select {
  background: #fff !important;
}
  
form[name*="misc"] select {
  background: #fff !important;
}  
  
form[name*="battle"] select {
  background: #fff !important;
} 
  
form[name*="mail"] select {
  background: #fff !important;
}
  
form[name*="berry"] select {
  background: #fff !important;
}  
  
form[name*="fossil"] select {
  background: #fff !important;
}  
  
.pics {
    margin-left: 20px;
  }
  
#menu {
  background: #383838;
  color: #49d;
}
  
#menu a {
  color: white;
}
  
#menu a:hover {
  color: #49d;
}

#menu img[src^="/Toolbar/headers/"][src$=".gif"] {
  filter: hue-rotate(75deg) !important;
}
  
#menu input {
    background: #49d;
    border: 2px solid 09f;
    border-radius: 5px 0 5px 0;
  }
}

@-moz-document url("http://www.serebii.net/index2.shtml"){

tr td:first-child + td + td div {
  display: none;
}

.fooevo {
    background: #49d !important;
  }  

.pics {
    margin-left: 20px;
  }
  
#menu img {
  filter: hue-rotate(75deg) !important;
}

img[src="/anime/pokemon/251.gif"] {
  display: none;
}
  
tr td:first-child + td {
  padding-left: 20px;
  padding-right: 20px;
}
}
