css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.scoop.it") {

html, body, #headbanner, #theme-actions-banner, #themeBanner .theme-header, #themeDescription, .block-content, .navtab-menu
{color: #919191 !important;
background: #111 !important;}
  
h1, h2, h3, h4, h5, h6, blockquote, .post-description, #themeAuthor, .post-metas
{color: #919191 !important;}    
  
a
{color: #00adee !important;}

a:visited 
{color: #00668c !important;} 
  
.footer, .inline-scoopit-window, #customHTML, .post-bottom-colored, .uv-icon.uv-bottom-right, #theme-toolbox, #headbanner, #theme-actions-banner, .vote-badge.vote-badge-header-center, .rescoop-container, .post .post-from
{display: none !important;}
  
.post
{color: #919191 !important;
background-color: #181818 !important;
height: 600px !important;
overflow: scroll !important;
padding: 5px !important;}
  
.thisistherealimage
{height: auto !important;}
  
.theme-post-list
{padding-bottom: 0px !important;}
  
.theme-post-list
{width: 100% !important;
padding-left: 100px !important;  
padding-bottom: 20px !important;} 
  
td
{display: flex !important;
flex-flow: row wrap !important; 
width: 100% !important;}

#utilArea 
{padding-bottom: 20px !important;}  
  
.pagination ul li a 
{border: 1px solid #00adee !important;}  

.pagination ul li.current a 
{background-color: #00668c !important;}  
  
}
