css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.ca"), domain("google.com") {

  /* Extra Search Results & Perma Ban Block Icon */
.g
{
  font-size: 1.05em;
  margin-right: -2%;
}

/* Result Stats */
#resultStats {
  font-size: 1.6em;
  font-weight:bold;
  color: red;
  background-color: #000000;
  padding-left: 1%;
}

/* Search Result Links */
#res h3 
{
  font-size: 1.1em;
  font-weight:bold;
}
  
/* Web|News|Videos|Images|More|Search Tools */
#hdtb-msb
{
  font-size: 1.5em;
  font-weight:bold;
}

/* Result Listed Date */
.f
{
  font-size: 1.05em;
  font-weight:bold;
  color: #ff0000;
  background-color: #97ffff;
}

/* Wikipedia Dropdowns */
.kv
{
  background-color: #ffffff;
}  
  
/* Additional Links listed in results */
._qgd, .hpn, .osl
{
  font-size: 1.1em;
}
  
/* url Listed Below Search result Links */
._Rm
{
  font-size: 1.03em;
  font-weight:bold;
  color:#004000;
}  

/* Result Descriptions */
.st
{
  font-size: 1.08em;
  line-height: 100%;
}  

  /* Additional Links listed in results */
._WQd
{
  font-size: 1.1em;
  font-weight:bold;
}

/* Additional Result Dates */
._pgd
{
  font-size: 1.05em;
  color: red;
  font-weight: bold;
}
  
/* visited link */
a:visited 
{
    color: #9999ff;
}

/* Hover Effect */
a:hover, a:focus
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}
    
/* Category Links hover effect */
.hdtbItm label:hover, .hdtbItm a:hover, #hdtb-more-mn a:hover, #hdtb .hdtb-mitem a:hover, .hdtb-mn-hd:hover, #hdtb-more:hover, #hdtb-tls:hover,
#hdtb .hdtb_mitem a:hover, a:focus,
.mn-hd-txt a:hover, a:focus
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}

/* Line 6 */
#hdtb_more_mn a:hover, .hdtbItm.hdtbSel:hover, .hdtbItm a:hover, #cdrlnk:hover
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}

/* Line 6 */
#gb#gb a.gb_l:hover, #gb#gb a.gb_l:focus
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}

/* BOTTOM NAVIGATION "Use precise location" & "Learn More" */
#swml a:hover
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}    
    
/* BOTTOM NAVIGATION Help | Send feedback | Privacy | Terms | Use Google.com */
.fbar a:hover, #fsett a:hover
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}    

/* Numbered Page Results @ Bottom */
.osl a:hover, .gl a:hover, #tsf a:hover, a.mblink:hover, a.gl:hover, a.fl:hover, .slk a:hover, .bc a:hover, .flt:hover, a.flt:hover u, .tbotu:hover, .blg a:hover
{
  color: #FFFFFF;
  font-weight: bold;
  background-color: #920000;
}

/* Right Justification of Settings et al icons */
#cnt
{
  width: 100%;
}

/* Search results page numbers at Bottom */
#center_col, #cnt #foot
{
  width: 150%;
}

/* Results positioning */
#center_col
{
  margin-left: -138px;
  margin-right: -64px;
  padding-right: 0px;
  padding-left: 0px;
}

/* Related Searches Listed At The Bottom Of The Search Results Page */
.brs_col
{
  font-size: 1.3em;
}

/* Google Definitions */
.lr_dct_sf_h
{
  font-size: 1.7em;
}  
  
/* Google Definitions */
._Jig
{
  font-size: 1.3em;
  margin-right: -30px;
}
  
}
