css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("comicvine.gamespot.com") {
  
/* Wiki Summary */
p, ul
{
  margin-top: 0px;
  margin-bottom: 5px;
}

/* Issue Number Adjustments */
.editorial p
{
  color: #000000;
  font-size: 1.1em;
  line-height: 110%;
}

/* Issue Title Adjustments */
.issue-grid .issue-name
{
  color: #136cb2;
  font-weight:bold;
  font-size: .9em;
  line-height: 110%;
}

/* Issue Date Adjustments */
.issue-grid .issue-date
{
  font-weight:bold;
  font-size: 1.0em;
  color: #ff0000;
  line-height: 110%;
}

/* Issue List Adjustments */
img

{
  box-sizing: border-box; /* Issue Spacing */
}

.editorial > li, .editorial > dd, .editorial > dt
{
  line-height: 50%; /* Editorial Spacing */
  padding-top: 0px; /* Top of Issue List */
  padding-bottom: 3px; /* padding below issues */
}

.editorial.grid .imgboxart
{
  margin-bottom: 5px; /* Space between Issue Photo & Issue Number */
}  
  
}
