css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("app.plex.tv") {

.flash-video {
    position: absolute;
    top: 0pc;
    right: 0pc;
    bottom: 0pc;
    left: 0pc;
}

}

@-moz-document domain(/*[[localurl]]*/) {

.flash-video {
    position: absolute;
    top: 0pc;
    right: 0pc;
    bottom: 0pc;
    left: 0pc;
}

}

@-moz-document domain(/*[[remoteurl]]*/) {

.flash-video {
    position: absolute;
    top: 0pc;
    right: 0pc;
    bottom: 0pc;
    left: 0pc;
}

}
