css:
@-moz-document domain("www.gaiaonline.com") {
#hd,
#bd,
#ft,
.yui-g,
.yui-gb,
.yui-gc,
.yui-gd,
.yui-ge,
.yui-gf {
	zoom: 0;
	color: green;
}
#ugm_dumpster_text {
	color: lightgreen;
	text-decoration: none;
	top: -21px;
	left: 3px;
	position: relative;
	font-weight: bold;
	font-size: 12px;
}
#messages {
	display: none !important;
	background: white !important;
}
#gaia_header .hud-account .hud-item-new.avatarName {
	color: lightblue;
	text-align: right;
	padding: 10px 28px 0 13px;
	min-width: 66px;
	font-size: 14px;
	cursor: pointer;
}
#gaia_header .hud-stats .hud-item-value-new {
	color: lightgreen;
	background-image: url(http://s.cdn.gaiaonline.com/images/gaia_global/gaia_header/new_header/meter_repeat.jpg);
	background-repeat: repeat;
	padding-left: 20px;
	min-height: 22px;
	min-width: 40px;
	text-align: right;
	margin: 2px 0 2px -20px;
	float: right;
	display: block;
	height: 22px;
}
#gaia_header .header_content .notificationChanges .changeMessages ul li a:hover {
	color: darkgreen;
}
#gaia_header .user-dropdown-menu .dropdown-list-item a,
#gaia_header .user-dropdown-menu .dropdown-list-item a:link,
#gaia_header .user-dropdown-menu .dropdown-list-item a:active,
#gaia_header .user-dropdown-menu .dropdown-list-item a:visited {
	text-decoration: none;
	color: lightgreen;
	font-size: 13px;
	font-family: Arial;
}
#gaia_header .header_content .notificationChanges .changeMessages ul li a:link {
	color: green;
}
}
