css:
@-moz-document domain("forums.hardwarezone.com.sg") {
/* 
	Page 
	background - blue #336699
*/

#content {
    background: none #336699 !important;
}

a {
    color: #ffa800!important;
}
/* 
	Page main body
	background - black #000000
	text color - orange #ffa800
*/

body {
    background: none #000000!important;
    margin: 0px 0px 0px 0px;
}
/* 
	Page top
*/

#floating-toolbar {
    background: none #336699!important;
    color: #ffa800!important;
}

.subMenuBtn {
    background: none #000000!important;
    color: #ffa800!important;
}

.subParentBtn.subMenuParentBtn {
    background: none #000000!important;
    color: #ffa800!important;
}
/* 
	Thread elements
*/

.alt1 {
    background: none #000000!important;
    color: #ffa800!important;
}

.alt2 {
    background: none #000000!important;
    color: #ffa800!important;
}

.alt1Active {
    background: none #000000!important;
    color: #ffa800!important;
}

.smallfont {
    color: #ffffff!important;
}

.quote {
    background: none #000000!important;
    color: #ffa800!important;
}
/* 
	Thread or Forum Footer
*/

#footer {
    background: none #000000!important;
}
/* 
	Thread or Forum Title
*/

.header-gray {
    background: none #336699!important;
    color: #000000!important;
    margin: 0 0 10px 0px!important;
    padding: 0 0 0px 10px!important;
}
/* 
	Quick reply panel
*/

.panelsurround {
    background: none #000000!important;
    color: #ffa800!important;
}
/* 
	Facebook links
*/

.hwz-social-container {
    display: none!important;
}
/* 
	Crowdynews videos at page bottom
	.cnAdzerkDiv
*/

.hwz_crowdynews_container {
    display: none!important;
}

.cnAdzerkDiv {
    display: none!important;
}
/* 
	More interesting reads at page top
*/

.OUTBRAIN {
    display: none!important;
}
/* 
	Banner ads at page top
	#ads-leaderboard 
*/

#ads-leaderboard {
    display: none!important;
}
/* 
	Qoo10 ads near page top
	#hwz_qoo10_widget
*/

#hwz_qoo10_widget {
    display: none!important;
}
/* 
	Ads banner near page center
	#div-gpt-ad-native
*/

#div-gpt-ad-native {
    display: none!important;
}
/* 
	Ads banner on right side
	#right-ads
*/

#right-ads {
    display: none!important;
}

#hwz_ad_notice_cont {
    display: none!important;
}
/* 
	DNSUnlocker ads
*/

.sgsefvhuedc {
    display: none!important;
}

.visadd_trk_img {
    display: none!important;
}
/* 
	Banner ads near page bottom
	#sponsored-links
*/

#hwz-lbforum-wrapper {
    display: none!important;
}

#hwz-articles-wrapper {
    display: none!important;
}

#sponsored-links {
    display: none!important;
}

div[id|="div-gpt-ad"] {
    display: none!important;
}

img[src*="advertisement_t"] {
    display: none!important;
}

