css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://demo.shout-irc.com/) {
/* GREY */
  #main .chan,
  #windows .window,
  #windows .window::before,
  #windows .chat,
  #windows .chan::before,
  #chat .count,
  .user-mode,
  .chan .channel .active::before,
  .sidebar .users .names,
  #form,
  #form .inner,
  #form .input,
  #form .input #input,
  #form .input #nick
  {
    background: #2B2B2B;
  }
  
  #nick, #submit
  {
    background-image: linear-gradient(#2B2B2B, #1A1A1A);
  }

/* Send/Leave button */
  #chat .header .right .button,
  #form .inner #submit
  {
    background: Thistle;
  }
  
/* Connect page input */
  #connect .container .input
  {
    background: Wheat;
  }
  
/* Dark Blue */
  body,
  #main
  {
    background: #12151B;
  }
/* TEXT WHITE */
  body,
  input,
  #chat .search::-moz-placeholder
  {
    color: #FFF;
  }
  
}
