css:
@-moz-document domain("feedly.com") {
/*TOP*/
/*Upgrade Button*/
.small.pro.primary {
    display: none !important;
}

/*Free Plan*/
.plan {
    display: none !important;
}

/*LEFT SIDEBAR*/

/*Add button*/
[data-dot="sidebar-add-content"] {
    display: none !important;
}

/*Separator*/
#feedlyTabs:nth-child(2) {
    margin-bottom: 10px !important;
}

/*Space between My Feeds and More*/
#tagList + div {
    margin-top: 0px !important;
}

/*Integrations*/
#integrationstab_header {
    display: none !important;
}

/*Shared collections*/

#librarytab_header {
    display: none !important;
}

/*Supoort*/
[data-matching-uri="pro/support"],
[data-matching-uri="pro/support"] {
    display: none !important;
}

/*Upgrade orange button*/
.fx-button.primary.small {
    display: none !important;
}

/*RIGHT SIDEBAR*/

/*Ads*/
#proAdModule_part {
    display: none !important;
}

/*Related*/
.widget-related-feeds {
    display: none !important;
}
}
