css:
@-moz-document url-prefix("http://docs.godotengine.org/") {
body
{
    color: #ccc;
}

.wy-menu-vertical li.on a span.toctree-expand,
.wy-menu-vertical li.current>a span.toctree-expand,
.wy-menu-vertical li span.toctree-expand,
.wy-menu-vertical li.on a,
.wy-menu-vertical li.current>a
{
    color: #ccc;
}

.wy-menu-vertical header,
.wy-menu-vertical p.caption
{
    color: #ddd;
}

.wy-nav-content-wrap
{
    background-color: #26252B;
}

.wy-nav-content
{
    background-color: #2C2A35;
}

.rst-versions
{
    border-top: #403E49;
    background-color: #2C2A35;
}

.rst-versions .rst-current-version
{
    background-color: #26252B;
    border-right: #26252B;
}

/* Left submenu lvl4 */
.wy-menu-vertical li.toctree-l3.current li.toctree-l4>a
{
    background-color: #282730;
}
.wy-menu-vertical li.toctree-l3.current li.toctree-l4>a:hover
{
    background-color: #2a2932;
}

/* Left submenu lvl3 */
.wy-menu-vertical li.toctree-l2.current>a,
.wy-menu-vertical li.toctree-l2.current li.toctree-l3>a
{
    background-color: #313039;
    border-color: #313039;
}
.wy-menu-vertical li.toctree-l2.current>a:hover,
.wy-menu-vertical li.toctree-l2.current li.toctree-l3>a:hover
{
    background-color: #34333e;
    border-color: #343341;
}

/* Left submenu lvl2 */
.wy-menu-vertical li.on a,
.wy-menu-vertical li.current>a
{
    background-color: #35333e;
    border-color: #35333e;
}
.wy-menu-vertical li.current
{
    background-color: #35333E;
}
.wy-menu-vertical li.current a:hover
{
    background-color: #383640;
}
.wy-menu-vertical li.current a
{
    border-right: #35333E;
}

/* Left submenu lvl1 */
.wy-nav-side,
.wy-menu-vertical
{
    background-color: #403E49;
    border-color: #403E49;
}
.wy-menu-vertical a:hover
{
    background-color: #45434E;
}

.codeblock,
pre.literal-block,
.rst-content .literal-block,
.rst-content pre.literal-block,
div[class^='highlight'],
code,
.rst-content tt,
.rst-content code
{
    background-color: #26252B;
    border-color: #16151B;
}
.n
{
    color: #ddd;
}
div[class^='highlight'] pre
{
    color: #eee;
}
.btn-neutral:hover,
.btn-neutral,
.btn-neutral:visited
{
    background-color: #404040 !important;
    color: #f3f6f6 !important;
}

/* table odd */
.wy-table-bordered-all, .rst-content table.docutils,
.wy-table-bordered-all td, .rst-content table.docutils td
{
    border-color: #16151B;
}

.wy-table-odd td, .wy-table-striped tr:nth-child(2n-1) td, .rst-content table.docutils:not(.field-list) tr:nth-child(2n-1) td
{
    background-color: #26252B;
}
}
