@-moz-document domain("xataka.com"), domain("xatakamovil.com"), domain("xatakafoto.com"), domain("xatakandroid.com"), domain("xatakahome.com"), domain("xatakawindows.com"), domain("xatakaciencia.com"), domain("applesfera.com"), domain("vidaextra.com"), domain("genbeta.com"), domain("genbetadev.com"), domain("magnet.xataka.com"), domain("compradiccion.com"), domain("esports.xataka.com"), domain("trendencias.com"), domain("belleza.trendencias.com"), domain("trendenciashombre.com"), domain("directoalpaladar.com"), domain("bebesymas.com"), domain("vitonica.com"), domain("decoracion.trendencias.com"), domain("poprosa.com"), domain("motorpasion.com"), domain("motorpasionmoto.com"), domain("motorpasionfuturo.com"), domain("elblogsalmon.com"), domain("pymesyautonomos.com"), domain("espinof.com"), domain("diariodelviajero.com"), domain("papelenblanco.com"), domain("xataka.com.mx"), domain("xataka.com.co"), domain("directoalpaladar.com.mx"), domain("motorpasion.com.mx") {
/* Comentarios */
#recommendedPosts,
#editorialRecommendedPosts,
.foot {
	display: none;
}
.comment-thread-close {
	z-index: 99;
	position: relative;
	margin-top: 1.5em;
	margin-right: 3.5em;
}
.comment-item {
	background-color: #fbfbfb;
}
.comment-reply {
	margin-top: 1.5em;
}
.comment-reply-relation {
	display: flex;
	top: -19px;
	left: 0;
	padding: 5px 10px 5px 10px;
	border-radius: 10px;
}
.comment-reply-relation .user-avatar {
	order: 1;
	margin-left: .7em;
	margin-right: .3em;
}
.comment-reply-relation a {
	order: 1;
}
.comment-reply-1,
.comment-reply-1 .comment-reply-relation {
	background-color: hsl(200,20%,96%);
}
.comment-reply-2,
.comment-reply-2 .comment-reply-relation {
	background-color: hsl(246,30%,96%);
}
.comment-reply-3,
.comment-reply-3 .comment-reply-relation {
	background-color: hsl(300,25%,95%);
}
.comment-content p {
	font-size: .9em;
	color: #384a54;
}
.comment-actions-abuse,
.comment-actions-vote,
.comment-actions-share {
	visibility: visible;
}

/*Media query*/
@media only screen and (min-width: 1280px) {
	.comment-actions-abuse a {
		opacity: .3;
	}
	.comment-actions-abuse a:hover {
		opacity: 1;
	}
}
@media only screen and (max-width: 1279px) {
	.comment-actions ul {
		display: block;
		min-width: 280px;
		width: auto;
		bottom: 0;
	}
	.comment-actions ul li,
	.comment-actions ul li a,
	.comment-actions ul li span {
		display: inline-block !important;
		line-height: normal;
		height: auto;
		padding: 0 5px;
		border: none;
		margin: 0 !important;
	}
	.comment-actions ul li a {
		width: 1em;
		height: 1.1em;
		overflow: hidden;
	}
	.context-menu-list .btn-comment-vote-up,
	.context-menu-list .btn-comment-vote-down {
		padding: 5px 5px 6px 7px;
		vertical-align: -7px;
	}
	.context-menu-list {
		background-color: #f3f3f3;
	}
	.context-menu-list li {
		color: #555;
	}
	.context-menu-list a:not(.btn-comment-vote-up):not(.btn-comment-vote-down) {
		color: #5c9124
	}
	.context-menu-list a:hover {
		background-color: transparent;
		opacity: .5;
	}
}

/*Media Query*/
}

