/* Artículo */
  
/*[[mejorar_lectura]]*/
  
  
.article img[class^="centro"] {
	max-width: 95%;
	box-shadow: 0px 0px 5px 0px hsla(201, 33%, 33%, .5);
	border-radius: 3px;
}
.article p code {
	background-color: #fff;
	padding: 0 .3em;
	font-size-adjust: .5;
}
.section-instagram-container,
.article-social-share.is-visible {
	display: none !important;
}
}

