css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("forum.dotclear.org"),
domain("dotclear.org") {

#announce.block, .pun th, #brdtitle, #brdfooternav,
fieldset, #featured, #userlist, .box, .forminfo, 
.blockpost h2, #users1 .tc2
{
background-color: #0000FF !important;
}
#d-userinfo, #d-userinfo, .roweven td,
.postright, .postleft
{
background: #222222 !important;
}
.blockpost .box, .postright, .postfootright,
.postleft, .postfootleft, .blockpost label
{
background: #222222 !important;
}
#d-mainnav, #d-top h1 a, #d-top, .rowodd td, #brdwelcome, input, #getdotclear, textarea, select
{
background: #111111 !important;
}
#d-subnav, #d-mainnav li.active,
#d-footer, #d-subnav a:hover, #d-subnav a:focus,
#brdtitle h1 a:hover, .inbox, #login
{
background: #0000FF !important;
}
a:link, a:visited
{
background: none !important;
}
body, #d-sitesmap ul ul li, #d-sitesmap
{
background: #000000 !important;
}
#announce.block, #brdwelcome
{
border: 3px #0000FF solid !important;
}
#d-subnav
{
border-bottom: 1px #0000FF solid !important;
}
a:link, a:visited
{
border-bottom: none !important;
}
.postright,.postfootright, select
{
border-color: transparent !important;
}
#d-userinfo input[type=text], #d-userinfo input[type=password],
.pun td, .pun th, .box, fieldset, input, .forminfo, textarea
{
border-color: #0000FF !important;
}
.pagelink 
{
padding-left: 5px !important;
}
.postlink 
{
padding-right: 5px !important;
}
h1, h1 a:link, h1 a:visited, h2, h3, h4, h5, h6
{
color: #0000FF !important;
}
body, #d-subnav .active a, #d-subnav a, #d-top,
#d-sitesmap a, #d-sitesmap ul li, #d-sitesmap ul,
#d-subnav a:hover, #d-subnav a:focus, #d-subnav ul,
#d-mainnav .active :link, #d-mainnav .active :visited,
#d-mainnav :link, #d-mainnav :visited, #d-mainnav a,
* html #d-mainnav :link, * html #d-mainnav :visited,
#brdtitle h1 a, a:link, a:visited, input, h2, .f-extend,
.blockpost dt strong a, .tcl h3 a, .f-gpl, .f-lang,
.f-pages, .f-users, .f-install, .f-media, .f-aspect, 
.f-antispam, legend, .postsignature p, textarea, select,
a:link, a:visited
{
color: #FFFFFF !important;
}
.stickytext
{
color: #FFFF00 !important;
}
a:hover
{
text-decoration: underline !important;
background: none !important;
}
}
