css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soundcloud.com") {

  body{
    background:url(http://www.nmgncp.com/data/out/97/4373013-deep-space-wallpaper-background.png) no-repeat center fixed;
    background-size:cover;
    -o-background-size:cover;
    -webkit-background-size:cover;
    padding: none !important;
  }
  
  *{
    border-radius: 0px !important;
    color: silver !important;
  }
  
  .l-fluid-fixed .l-sidebar-right{
    width: 316px !important;
    background: rgba(0,0,0,0.64) !important;
    padding: 8px !important;
    top: 0px !important;
  }
  
  .sound__soundActions{
    margin-top: 32px !important;
    background-color: none !important;
  }
  
  .sc-toggle{
    background: rgba(0,0,0,0) !important;
    border: 2px solid silver !important;
    padding: 1px !important;
  }
  
  .sc-toggle::before{
    border-radius: 0px !important;
    padding-right: 1px !important;
    outline: 2px solid black !important;
  }
  
  .sc-toggle-handle{
    background: rgba(0,0,0,1) !important;
    color: black;
    width: 27px !important;
    height: 25px !important;
    top: 0px !important;
  }
  
  .soundList__item {
    clear: both;
    margin-bottom: 36px;
    position: relative;
    padding: 4px !important;
    border: 1px solid silver !important;
}
  
  .l-footer{
    background: rgba(0,0,0,0.64) !important;
    padding: 8px !important;
    text-align: center !important;
  }
  
  .audibleEditForm__form, .sc-button, .sc-tag, textarea, .moreActions__button, .moreActions__link, .sc-background-light, .tagInput__wrapper, input{
    background: rgba(0,0,0,0.64) !important;
    color: white !important;
  }
  
  .sc-ir{
    filter: invert(1);
        -webkit-filter: invert(1);
        -moz-filter: invert(1);
        -o-filter: invert(1);
        -ms-filter: invert(1);
  }
  
  .l-main{
    background: rgba(0,0,0,0.64) !important;
    padding: 8px !important;
  }
  
  .uploadMain__chooserContainer{
    background: rgba(0,0,0,0.64) !important;
  }
  
  .uploadMain__foot{
    background: rgba(0,0,0,0.64) !important;
  }
  
  .chartTrack:hover, .sc-button-small, .compactTrackListItem.clickToPlay:hover {
    background-color: rgba(242, 242, 242, 0.17);
    cursor: pointer;
}
  .compactTrackList__moreLink:hover{
    background-color: rgba(242, 242, 242, 0.17);
    color: white !important;
  }
  
  .trackItem.hover, .trackItem.active{
    background: rgba(0,0,0,0.56) !important;
    margin: 4px 0px 4px 0px !important;
    border: 1px solid #F50 !important;
  }
  
  .l-fixed-top-one-column > .l-top{ 
    background: rgba(0,0,0,0.4) !important;
  }
  
  .l-about-main{
    background: rgba(0,0,0,0.4) !important;
    padding-left: 48px !important;
    margin-left: -30px !important;
    
  }
  
  .trackItem.hover:not(.m-disabled) .trackItem__additional, .trackItem.active:not(.m-disabled) .trackItem__additional{
    background: rgba(0,0,0,0) !important;
    margin: 0px 0px 0px 0px !important;
    padding-left: 5px !important;
  }
  
  .conversation {
    position: relative !important;
    width: 713px !important;
    left: -31px !important;
    padding-left: 29px !important;
    padding-right: 16px !important;
    padding-bottom: 16px !important;
    background: rgba(0,0,0,0.64) !important;
}
  
  .listenEngagement{
    padding: 8px !important;
    border: none !important;
  }
  
  .compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional, .sound__soundActions, .compactTrackListItem.clickToPlay.active, .compactTrackListItem.clickToPlay.active .compactTrackListItem__additional, .playControls__bg{
    background: none !important;
    background-color: none !important;
    color: white !important;
  }
  
  .l-container.l-content{
    padding-top: 8px !important;
    margin-top: 46px !important;
    background: transparent !important;
  }
  
  .editTrackItem__additional, .modal.modalWhiteout {
    background: transparent !important;;
  }
    
  .playControls__inner, .linkMenu, .dialog, .header{
    background: rgba(0,0,0,0.92) !important;
    border-top: 1px solid silver !important;
  }
  
  a:visited{
    color: #f50 !important;
  }
  
  .commentForm__wrapper, .soundBadge__additional{
    background: none !important;
    border: none !important;
  }
  
  .commentForm__input{
    background: rgba(0,0,0,0.64) !important;
    color: white !important;
  }
  
  .visuals.streamContext{
    width: 99% !important;
  }
  
  .featureChart__planWrapper{
    background-color: rgba(0,0,0,0.64) !important;
    color: white !important;
    border: 1px solid silver !important;
  }
  
  .mobileAppsButtons.m-sidebar{
    padding-left: 24px !important;
  }
  
  .dashbox{
    display: none !important;
  }
  
  .l-fixed-width > .l-main{
    margin-top: 8px !important;
  }
  
  .dropbar__content{
    background-color: rgba(0,0,0,0.96) !important;
  }
  
  .conversation__actions{
    background: rgba(0,0,0,0.6) !important;
    padding-top: 34px !important;
    top: 47px !important;
  }
  
  .modal__modal, .tabs__tabs, .g-modal-section{
    background: rgba(0,0,0,0.64) !important;
    border: 1px solid silver !important;
  }
  
  .gritter-image {
    top: 2px !important;
    left: 2px !important;
  }
  
  .gritter-item-wrapper{
    background-color: rgba(0,0,0,0.64) !important;
  }
  
  
  .tabs__tabs, .g-modal-section{
    border: none !important;
  }
  
  .conversationBadge{
    background: rgba(0,0,0,0.64) !important;
    padding: 8px !important;
  }
  
  /* FONTS */
  
  .sc-ministats-small{
    color: white !important;
  }
  
  .sc-button-active:hover, .sc-button-active:focus, .sc-button-active.sc-button-focus, .sc-button-selected:hover, .sc-button-selected:focus, .sc-button-selected.sc-button-focus, .sc-status-icon-creator {
    color: #f50 !important;
    border: 1px solid #f50 !important;
    filter : invert(none) !important;
}
  
  span{
    color: white !important;
  }
  
  a.sc-link-dark{
    color: white !important;
    text-transform: uppercase !important;
    font-weight: none !important;
    font-size: 15px !important;
  }
  
  a.sc-link-dark:hover{
    color: #747474 !important;
  }
  
  .sc-truncate, .g-tabs-link{
    color: white !important;
  }
  
  .g-tabs-link:hover, .g-tabs-link.active{
    color: #FF7000 !important;
  }
  
  .g-tabs-link:focus{
    color: #787878 !important;
  }
  
  .volume__sliderWrapper{
    background-color: rgba(69,69,69,0.64) !important;
  }
  
  .collectionSection__heading{
    color: white !important;
    margin-left: 8px !important;
    margin-top: 8px !important;
  }
  
  p{
    color: white !important;
  }
  
  .premiumContent__copyHeader{
    color: white !important;
  }
  
  .sc-button:disabled, .sc-button:disabled:hover{
    color: black !important;
  }
  
  h1{
    color: white !important;
  }
  
  .composeTextfield .textfield__input{
    color: black !important;
  }
  
}

@-moz-document url("https://soundcloud.com/terms-of-use") {

  a{
    color: orange !important;
  }
  
  h2{
    color: white !important;
  }
}

@-moz-document url("https://soundcloud.com/pages/copyright"){
  a{
    color: orange !important;
  }
  
  h2{
    color: white !important;
  }
  
  .sidebarInfoBox{
    background: transparent !important;
  }
  
  .sidebarInfoBox__text{
    color: white !important;
  }
}

@-moz-document url("https://soundcloud.com/go") {

  a{
    color: orange !important;
  }
  
  h2{
    color: white !important;
  }
}

@-moz-document url("https://soundcloud.com/mobile") {

  a{
    color: orange !important;
  }
  
  h2{
    color: white !important;
  }
}
