css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.topit.me/") {
* {
    font-weight: 500;
}
a {
    text-decoration: none !important;
}
#content .pageheader h2 {
    font-size: 18px!important;
}
#content .pageheader p {
    font-size: 15px!important;
}
/*logo*/

#logo {
    animation: flipOutX 2S linear infinite alternate;
    position: relative;
    left: -280px;
}
@keyframes flipOutX {
    0% {
        opacity: 1;
        transform: perspective(400px) rotateX(0deg);
    }
    50% {
        opacity: 0;
        transform: perspective(400px) rotateX(90deg);
    }
    100% {
        opacity: 1;
        transform: perspective(400px) rotateX(180deg);
    }
}
/*用户条*/

#menu {
    margin-left: 100px!important;
    background-color: transparent;
}
#menu li a {
    color: #FF4F73;
    font-size: 18px!important;
    font-weight: 500;
}
#loggeduser ul a {
    color: #FF4F73;
    font-size: 14px!important;
    font-weight: 500;
}
#loggeduser > h2 > a {
    color: #FF4F73;
}
/*侧边*/

.i_wall {
    color: #fff!important;
    background-image: none;
    background-color: #FF5F83;
    padding: 3px 6px 3px 6px;
    border-radius: 2px;
}
.i_wall:hover {
    background: #f31e4e;
}
.operbox ul a {
    margin-left: 25px!important;
}
/*foot*/

#pagination {
    background: transparent;
}
#pagination > div > span.dot {
    background: #fff;
    border: none;
}
#pagination > div > a,
#pagination .current {
    border-radius: 6px;
    border: 1px solid rgb(220, 220, 220);
    cursor: pointer;
    color: #FF4F73;
    font-size: 15px;
    font-weight: bold;
    text-decoration: none;
    text-shadow: 0px 1px 0px rgb(255, 255, 255);
}
#pagination .current {
    background-color: #F6EBF1;
}
#pagination a:hover {
    background: #e6c1b3;
    color: #fff;
}
#pagination > div > form {
    color: #FF4F73!important;
    font-size: 16px!important;
}
#pagination > div > form .page_number_input {
    color: #FF4F73!important;
    font-size: 16px!important;
    width: 16px;
}
#page_submit,
#page {
    background-color: #fff!important;
    color: #FF4F73!important;
    font-size: 16px!important;
}
#page {
    margin-left: 3px;
    padding: 0px!important;
    border-radius: 2px;
    background-color: transparent!important;
    color: #fff;
}
#page:focus {
    background-color: #fff!important;
}
#page_submit {
    margin-left: 4px!important;
    padding: 0px!important;
    border-radius: 4px;
    border-width: 0px!important;
    background-color: #F6EBF1!important;
}
/*隐藏*/

#footer,
.box .button i,
#custom_02,
#sidebar > div.box.box_small,
#footer+div {
    display: none;
}
.m .bar {
    background: rgba(250, 250, 250, 0.8);
}
#content > div.pageheader > div > ul > li {
    font-size: 15px;
}
.tabs li a {
    font-size: 17px;
}
}

@-moz-document url("http://www.topit.me/"), url("http://www.topit.me/feed"), url-prefix("http://www.topit.me/?p="), url-prefix("http://www.topit.me/items/search?"), url-prefix("http://www.topit.me/user/"), url-prefix("http://www.topit.me/item/"), url-prefix("http://www.topit.me/album/") {
*,
#wrapper-inner {
    transition: all ease-out 0.25s;
}
#content > div.catalog > div img {
    -webkit-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
#content > .catalog .img:hover {
    -webkit-transform: scale(1.1);
    transform: scale(1.1);
}
#content > .catalog .img:hover {
    -webkit-transform: scale(1.1);
    transform: scale(1.1);
}
}

@-moz-document url("http://www.topit.me/topics"), url-prefix("http://www.topit.me/user/topic") {
#content {
    padding-right: 20px;
}
#content .post-box {
    text-align: center;
}
}

@-moz-document url("http://www.topit.me/feed") {
#content > div.pageheader > div > ul > li {
    font-size: 15px;
}
.cont {
    width: 920px;
    height: auto;
}
#content .cont img {
    width: 140px;
    height: auto;
}
}

@-moz-document regexp("^https?:\\/\\/www\\.topit\\.me((?!item\\/\\d+|users).)*") {
#wrapper-inner {
    width: 1350px;
}
#canvas {
    width: 1080px;
}
}

@-moz-document regexp("^https?:\\/\\/www\\.topit\\.me.*group.*") {
.cat_group .info {
    width: 700px;
}
.cat_group .items {
    width: 430px;
    float:right;
}
#wrapper-inner .cat_group{
	position:relative;
}
#wrapper-inner .cat_group h2{
    font-size:18px;
	position:absolute;
    top:5px;
}
#wrapper-inner .cat_group .info{
	margin-top:35px;
    width:870px;
}
#wrapper-inner .cat_group .info img{
    margin-top:0;
}
}

@-moz-document url-prefix("http://www.topit.me/users") {
#wrapper-inner {
    width: 1350px;
}
#canvas {
    width: 1080px;
}
#canvas .item{
	width:480px;
    height:130px;
    float:left;
    margin-top:0px;
  
}
#canvas .item:nth-child(even){
	margin-left:55px;
}
#users{
	margin-left:10px;
}
#users .item .content{
	width:410px;
    float:right;
}

#users .hearted span:after {
    content: '取消关注';
    color: #fff;
    background: #ff6484;
    display: inline-block;
    padding: 5px 10px;
    border-radius: 3px;
    border: 1px solid #ff6484;
}


}
