css:
@-moz-document domain("bb.pxl.be") {
div#topTabs, iframe#navFrame { 
	height: 62px !important;
    background-color: black;
}

div.brandingImgWrap {
    height: 25px;
}

div.brandingImgWrap > a {
    top: 9px;
}

img.bannerImage {
    height: 50px !important;
    max-height: 50px !important;
}

div.headertekst {
    height: 0px !important;
    line-height: 40px;
}

body {
    background-image: none !important;
    background-color: black !important;
}


body.container {
    background-color: inherit !important;
}

body.topFrame {
	overflow: hidden;
}

.container > .headerLogo {
    margin-top: inherit;
    height: 165px;
}

div#copyright {
	display: none !important;
}

.stream-detail-page {
 	 background-color: #dcdad5 !important;
}
}

@-moz-document url-prefix("https://epos.pxl.be/"), domain("fs3.pxl.be"), domain("stage.pxl.be") {
div#brandingWrapper, div#contentWrapper, .illustrationClass {
    background-color: #353535 !important;
    color: white !important;
    background-image: none !important;
}

body, html {
    background-color: #353535 !important;
    background-image: none !important;
}

.container > .subMenu > ul > li a:link, 
.container > .subMenu > ul > li a:active, 
.container > .subMenu > ul > li a:visited {
    color: white;
}

.container > .subMenu > ul > li a:hover {
 	color: #58A618 !important;   
}
}

@-moz-document url-prefix("https://bb.pxl.be/courses"), url-prefix("https://bb.pxl.be/bbcswebdav"), url-prefix("https://epos.pxl.be/Tools/BewijsActiviteit") {
body {
    background-color: white !important;
}
}
