@-moz-document domain("m.habrahabr.ru"), domain("m.geektimes.ru"), domain("m.megamozg.ru") {
.logo {
    /*
    width: 54px !important;
    height: 54px !important;
    */
    background-image: none !important;
    display: inline !important;
}
a {
    color: rgb(109, 163, 189) !important;
}
.logo:after {
    width: 54px !important;
    height: 54px !important;
    font-size: 40px !important;
    padding-left: 13px !important;
    padding-bottom: 10px !important;
    color: #D4D5D3 !important;
    display: inline-block !important;
}
body, .comments_list .comment_item, .comments_list .comment_item.bad .text {
    background: #2E3436 !important;
    color: #BABDB6 !important;
}
.comments_list .comment_item + .comment_item {
    border-top: 1px solid #BABDB6 !important;
}
.comments .text, .comments_list .comment_item.bad .text {
    opacity: 1.0 !important;
}
#header, .share_to_social_networks, #footer, #footer .links a, .copyright, a.post_inner:active, .list_item:active,  .post_show .bottom_buttons_panel, .text_html pre code {
    background: #272C2D !important;
    color: #BABDB6 !important;
}
.title, .text, h2, h1, h3, h4, h5, h6, .username {
    color: #BABDB6 !important;
}
#sidebar .username {
    color: rgb(99, 99, 99) !important;
}
.btn, a.btn, .comments_count {
    background: #555753 !important;
    background-color: #555753 !important;
    background-image: none !important;
    color: rgb(238, 238, 236) !important;
    border: 1px solid rgb(212, 212, 212) !important;
    box-shadow: none !important;
    text-shadow: none !important;
}
.post_show .bottom_buttons_panel .save_buttons a.favorite {
    margin-right: 101px !important;
}
.comments_count {
    background-color: #555753 !important;
    color: rgb(238, 238, 236) !important;
    border: none !important;
}
.comments_count.has_new {
    background-color: #B8B8B6 !important;
    color: #555753 !important;
    border-color: #272C2D !important;
}
.comments_count {
    border-radius: 30px !important;
    width: 40px !important;
    height: 40px !important;
    vertical-align: bottom !important;
    padding-top: 13px !important;
    display: inline-block !important;
}
.copyright, .similar_posts .title {
    border: none !important;
}
.flag, a.edit:after {
    background-image: none !important;
    font-size: 12px !important;
    vertical-align: top !important;
    background: #555753 !important;
    color: rgb(238, 238, 236) !important;
    border-radius: 2px !important;
    margin-top: 3px !important;
}
.posts .post, .list_items .list_item, .comments .comment + .comment {
    border-color: #BABDB6 !important;
}
}

