css:
@-moz-document domain("obsidianportal.com") {
/* Consistency - the hobgoblin of small minds */

/* HEADER FIXES */

.campaign-public-header,
.campaign-public-header .flame-dragon,
.campaign-public-header .flame-dragon svg {
    height: 96px;
}

.campaign-public-layout #campaign-summary-row .campaign-title {
    position: inherit;
    font-size: 2.75em;
    margin-left: 15px;
    line-height: 95px;
}

.campaign-public-layout #banner-container,
.campaign-public-layout .highlight-container{
    display: none;
}


.campaign-public-layout #campaign-summary-row .campaign-public-title-container {
    height: 80px;
    float: inherit;
}

/* CONTENT AREA FIXES */

.campaign-public-layout #content {
    max-width: none;
    margin: 0;
}

.main-content {
    padding-left: 1em;
    background-color: #fff;
}

.main-content-container {
    padding-left: 0 !important;
}


/* NAVIGATION AREA FIXES */

.collapsable-nav-row .front-nav-container {
    padding-left: 0;
    padding-right: 0;
    width: 200px;
}

.campaign-public-layout #campaign-nav {
    font-weight: normal;
    width: 100%;
    background-color: inherit;
}

.campaign-public-layout #campaign-nav li {
    height: inherit;
}

.campaign-public-layout #campaign-nav .nav-links li.active {
    background-color: #333;
}

.campaign-public-layout #campaign-nav .nav-links li.active:after {
    right: 0;
    border-right-color: white;
    border-right: 14px solid white;
    border-left: none;
}

.campaign-public-layout #campaign-nav li a {
padding: 5px 0 5px 30px;
    height: 38px;
    line-height: inherit;
    font-size: .9em;
    color: #bbb;
}

.campaign-public-layout #campaign-nav .icon {
    font-size: 24px;
    width: 25px;
    line-height: 24px;
    background-color: inherit;
    text-align: inherit;
}

.campaign-public-layout #campaign-nav .nav-text {
    display: inline-block;
    padding-left: 15px;
    line-height: 24px;
    text-indent: -.5em;
    padding-top: 5px;
}




.campaign-public-layout .collapsable-nav-row {
    display: flex ;
    background-color: #111;
}

}
