@-moz-document regexp("https?://accounts.google.com/.*") {
/*
name : Gmail Dark Login Page ( v1 & v2 )
author : tm1
version : 2.1
date : 2019-12-22
url 1 : https://github.com/... ?
url 2 : https://userstyles.org/styles/132960/gmail-dark-login-page
url 3 : ... ?

Notes from latest update:

2016-09-22   v. 1.0 :
- Created

2018-07-22   v. 2.0 :
- Updated

2019-12-22   v. 2.1 :
- Updated

*/
html, body {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
}
/* main header block */
.google-header-bar, .H2SoFe::before, .uc81Ff::before {
    background-color:rgba(16, 16, 32, 0.3) !important;
}
/* main footer block */
.H2SoFe::after, .uc81Ff::after {
    background-color:rgba(16, 16, 32, 0.3) !important;
}
/* main block */
.H2SoFe, .uc81Ff {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
}
/* InitialView block sub div - signinchooser */
.xkfVF, .FH0XAb, .RgEUV {
    background-color:rgba(64, 16, 32, 0.1) !important;
}
/* InitialView block sub div - default */
.RgEUV {
    border: 1px solid #dadce0 !important;
}
/* signup block - choose account text */
.jXeDnc h1,  .pYERqe /*, h1 */ {
    color: #756575 !important;
}

/* main block wrapper */
.VmOpGe {
    background: transparent !important;
}
/* language list element */
.u7land .B9IrJb {
    color: rgba(128, 128, 128, 0.8) !important;
    background-color:rgba(32, 32, 32, 0.9) !important;
}
/* hovered language element */
.u7land .B9IrJb:hover {
    color: rgba(128, 128, 128, 0.8) !important;
    background-color:rgba(48, 48, 48, 0.9) !important;
}
/* selected language element */
.u7land .B9IrJb[aria-selected="true"] {
    color: rgba(192, 128, 128, 0.8) !important;
    background-color:rgba(48, 48, 48, 0.9) !important;
}
/* language and other blocks */
div[role="listbox"] {
    color: rgba(192, 128, 128, 0.8) !important;
    background-color:rgba(64, 16, 32, 0.9) !important;
}
/* action header block */
div[jsname="paFcre"] {
    color: rgba(128, 128, 128, 0.8) !important;
    background-color:rgba(64, 16, 32, 0.1) !important;
}
/* google logo block */
.banner, .fctIrd {
    /* background: #272C2D !important; - orig */
    color: rgba(192, 128, 128, 0.8) !important;
    background-color:rgba(64, 16, 32, 0.1) !important;
}
.card {
    background: #272C2D !important;
}
/* signup block */
.signup-box, .RAYh1e, .LJtPoc {
    color: rgba(128, 128, 128, 0.8) !important;
    /* background: #272C2D !important; - orig */
    background-color:rgba(32, 16, 64, 0.1) !important;
}
/* accounts list block */
.bCAAsb, .Fk98Qd {
    color: rgba(128, 128, 128, 0.8) !important;
    /* background: #272C2D !important; - orig */
    background-color:rgba(32, 32, 32, 0.1) !important;
}
/* accounts list elements */
.tnNfDe, .JDAKTe {
    color: rgba(32, 32, 128, 0.8) !important;
    /* background: #272C2D !important; - orig */
    background-color:rgba(32, 32, 32, 0.1) !important;
}
.lCoei[role="button"]:hover, .lCoei[role="link"]:hover, .tnNfDe:hover, .oTR3Cf:hover, .V2qUud:hover {
    color: rgba(32, 32, 128, 0.8) !important;
    /* background: #272C2D !important; - orig */
    background-color:rgba(64, 64, 64, 0.5) !important;
}
/* accounts list elements - name */
.w1I7fb, .tRmeue {
    color: #F55 !important;
    /* background: #272C2D !important; - orig */
}
/* accounts list elements - email */
.wLBAL, .gu0k2c {
    color: #5a5 !important;
    /* background: #272C2D !important; - orig */
}
/* accounts list elements - actions */
.BHzsHc {
    color: #1a73e8 !important;
    /* background: #272C2D !important; - orig */
}
/* accounts list elements - buttons */
.oG5Srb:not(.RDPZE) {
    /* color: #1a73e8 !important; */
    /* background: #272C2D !important; - orig */
    background-color:rgba(64, 64, 64, 0.5) !important;
}
.oG5Srb:hover:not(.RDPZE) {
    /* color: #1a73e8 !important; */
    /* background: #272C2D !important; - orig */
    background-color:rgba(128, 128, 128, 0.5) !important;
}
/* elements - name */
.jXeDnc {
    color: #F55 !important;
    /* background: #272C2D !important; - orig */
}
/* elements - email */
.iiFyne {
    color: #5a5 !important;
    /* background: #272C2D !important; - orig */
}
/* elements - show password */
.wRNPwe {
    color: rgba(192, 192, 255, 0.9) !important;
    /* background: #272C2D !important; - orig */
}
.wRNPwe:hover {
    color: rgba(128, 128, 192, 0.9) !important;
    /* background: #272C2D !important; - orig */
}
input, textarea, select {
    color: rgba(128, 192, 128, 0.8) !important;
    background-color:rgba(64, 64, 64, 0.5) !important;
}
button {
    color: rgba(128, 128, 192, 0.8) !important;
    background-color:rgba(64, 64, 64, 0.5) !important;
}
button:focus, button:hover {
    color: rgba(128, 128, 192, 0.9) !important;
    background-color:rgba(64, 64, 64, 0.9) !important;
}
button::before:focus, button::before :hover {
    color: rgba(128, 128, 192, 0.9) !important;
    background-color:rgba(64, 64, 64, 0.9) !important;
}
/* remove account registration button */
.EACaeb li .q4UYxb {
    color: rgba(128, 128, 128, 0.8) !important;
    background-color:rgba(16, 16, 32, 0.7) !important;
}
.form-element strong {
    color: white !important;
    background: #272C2D !important;
}
/* account name block */
.signin-card #email-display, .XraQ3b {
    /* color: rgb(216, 216, 216) !important; - orig */
    color: rgba(216, 216, 216, 0.8) !important;
    background-color:rgba(64, 64, 64, 0.2) !important;
}
.XraQ3b:focus, .XraQ3b:hover {
    color: rgb(216, 216, 216) !important;    
    /* color: rgba(192, 128, 128, 0.8) !important; */
    background-color:rgba(96, 96, 96, 0.3) !important;
}
/* end */
}

@-moz-document regexp("https?://www.google.com/accounts/recovery/.*") {
html, body {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
}
.google-header-bar {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
}
.main {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
}
h1, h2, h3, h4, h5, h6, strong {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
}
/* end */
}
